import{_ as e,o,c as d,e as c}from"./app.e2a3b496.js";const i={},l=c('<h1 id="mvc\u4E0Emvp\u53CAmvvm" tabindex="-1"><a class="header-anchor" href="#mvc\u4E0Emvp\u53CAmvvm" aria-hidden="true">#</a> MVC\u4E0EMVP\u53CAMVVM</h1><h2 id="mvc" tabindex="-1"><a class="header-anchor" href="#mvc" aria-hidden="true">#</a> MVC</h2><p><code>MVC</code>\u662F\u4E00\u79CD\u4F7F\u7528Model View Controller\u6A21\u578B-\u89C6\u56FE-\u63A7\u5236\u5668\u8BBE\u8BA1\u521B\u5EFA<code>Web</code>\u5E94\u7528\u7A0B\u5E8F\u7684\u6A21\u5F0F:</p><ul><li><code>Model</code>\uFF08\u6A21\u578B\uFF09\u8868\u793A\u5E94\u7528\u7A0B\u5E8F\u6838\u5FC3\uFF08\u6BD4\u5982\u6570\u636E\u5E93\u8BB0\u5F55\u5217\u8868\uFF09\u662F\u5E94\u7528\u7A0B\u5E8F\u4E2D\u7528\u4E8E\u5904\u7406\u5E94\u7528\u7A0B\u5E8F\u6570\u636E\u903B\u8F91\u7684\u90E8\u5206\u3002</li><li><code>View</code>\uFF08\u89C6\u56FE\uFF09\u663E\u793A\u6570\u636E\uFF08\u6570\u636E\u5E93\u8BB0\u5F55\uFF09\u662F\u5E94\u7528\u7A0B\u5E8F\u4E2D\u5904\u7406\u6570\u636E\u663E\u793A\u7684\u90E8\u5206\u3002</li><li><code>Controller</code>\uFF08\u63A7\u5236\u5668\uFF09\u5904\u7406\u8F93\u5165\uFF08\u5199\u5165\u6570\u636E\u5E93\u8BB0\u5F55\uFF09\u5E94\u7528\u7A0B\u5E8F\u4E2D\u5904\u7406\u7528\u6237\u4EA4\u4E92\u7684\u90E8\u5206\u3002</li></ul><p>MVC \u5206\u5C42\u6709\u52A9\u4E8E\u7BA1\u7406\u590D\u6742\u7684\u5E94\u7528\u7A0B\u5E8F\uFF0C\u56E0\u4E3A\u60A8\u53EF\u4EE5\u5728\u4E00\u4E2A\u65F6\u95F4\u5185\u4E13\u95E8\u5173\u6CE8\u4E00\u4E2A\u65B9\u9762\u3002\u4F8B\u5982\uFF0C\u60A8\u53EF\u4EE5\u5728\u4E0D\u4F9D\u8D56\u4E1A\u52A1\u903B\u8F91\u7684\u60C5\u51B5\u4E0B\u4E13\u6CE8\u4E8E\u89C6\u56FE\u8BBE\u8BA1\u3002\u540C\u65F6\u4E5F\u8BA9\u5E94\u7528\u7A0B\u5E8F\u7684\u6D4B\u8BD5\u66F4\u52A0\u5BB9\u6613\u3002 MVC \u5206\u5C42\u540C\u65F6\u4E5F\u7B80\u5316\u4E86\u5206\u7EC4\u5F00\u53D1\u3002\u4E0D\u540C\u7684\u5F00\u53D1\u4EBA\u5458\u53EF\u540C\u65F6\u5F00\u53D1\u89C6\u56FE\u3001\u63A7\u5236\u5668\u903B\u8F91\u548C\u4E1A\u52A1\u903B\u8F91\u3002</p><ul><li><p>\u4F18\u70B9</p><ul><li>\u8026\u5408\u6027\u4F4E</li><li>\u91CD\u7528\u6027\u9AD8</li><li>\u53EF\u7EF4\u62A4\u6027\u9AD8</li><li>\u6709\u5229\u8F6F\u4EF6\u5DE5\u7A0B\u5316\u7BA1\u7406</li></ul></li><li><p>\u7F3A\u70B9</p><ul><li>\u6CA1\u6709\u660E\u786E\u7684\u5B9A\u4E49</li><li>\u89C6\u56FE\u4E0E\u63A7\u5236\u5668\u95F4\u7684\u8FC7\u4E8E\u7D27\u5BC6\u7684\u8FDE\u63A5</li><li>\u589E\u52A0\u7CFB\u7EDF\u7ED3\u6784\u548C\u5B9E\u73B0\u7684\u590D\u6742\u6027</li></ul></li></ul><p><img src="https://github.com/CharonChui/Pictures/blob/master/mvc_model.png?raw=true" alt="image"></p><h2 id="mvp" tabindex="-1"><a class="header-anchor" href="#mvp" aria-hidden="true">#</a> MVP</h2><p><code>MVP</code>\u662F\u4ECE\u7ECF\u5178\u7684\u6A21\u5F0F<code>MVC</code>\u6F14\u53D8\u800C\u6765\uFF0C\u5B83\u4EEC\u7684\u57FA\u672C\u601D\u60F3\u6709\u76F8\u901A\u7684\u5730\u65B9\uFF1A<code>Controller/Presenter</code>\u8D1F\u8D23\u903B\u8F91\u7684\u5904\u7406\uFF0C<code>Model</code>\u63D0\u4F9B\u6570\u636E\uFF0C<code>View</code>\u8D1F\u8D23\u663E\u793A\u3002 \u4F5C\u4E3A\u4E00\u79CD\u65B0\u7684\u6A21\u5F0F\uFF0C<code>MVP</code>\u4E0E<code>MVC</code>\u6709\u7740\u4E00\u4E2A\u91CD\u5927\u7684\u533A\u522B\uFF1A\u5728<code>MVP</code>\u4E2D<code>View</code>\u5E76\u4E0D\u76F4\u63A5\u4F7F\u7528<code>Model</code>\uFF0C\u5B83\u4EEC\u4E4B\u95F4\u7684\u901A\u4FE1\u662F\u901A\u8FC7<code>Presenter</code>(<code>MVC</code>\u4E2D\u7684<code>Controller</code>)\u6765\u8FDB\u884C\u7684\uFF0C \u6240\u6709\u7684\u4EA4\u4E92\u90FD\u53D1\u751F\u5728<code>Presenter</code>\u5185\u90E8\uFF0C\u800C\u5728<code>MVC</code>\u4E2D<code>View</code>\u4F1A\u76F4\u63A5\u4ECE<code>Model</code>\u4E2D\u8BFB\u53D6\u6570\u636E\u800C\u4E0D\u662F\u901A\u8FC7<code>Controller</code>\u3002 \u5728<code>MVC</code>\u91CC,<code>View</code>\u662F\u53EF\u4EE5\u76F4\u63A5\u8BBF\u95EE<code>Model</code>\u7684\uFF01\u4ECE\u800C\uFF0C<code>View</code>\u91CC\u4F1A\u5305\u542B<code>Model</code>\u4FE1\u606F\uFF0C\u4E0D\u53EF\u907F\u514D\u7684\u8FD8\u8981\u5305\u62EC\u4E00\u4E9B\u4E1A\u52A1\u903B\u8F91\u3002 \u5728<code>MVC</code>\u6A21\u578B\u91CC\uFF0C\u66F4\u5173\u6CE8\u7684<code>Model</code>\u7684\u4E0D\u53D8\uFF0C\u800C\u540C\u65F6\u6709\u591A\u4E2A\u5BF9<code>Model</code>\u7684\u4E0D\u540C\u663E\u793A\u53CA<code>View</code>\u3002\u6240\u4EE5\uFF0C\u5728<code>MVC</code>\u6A21\u578B\u91CC\uFF0C<code>Model</code>\u4E0D\u4F9D\u8D56\u4E8E<code>View</code>\uFF0C\u4F46\u662F<code>View</code>\u662F\u4F9D\u8D56\u4E8E<code>Model</code>\u7684\u3002 \u4E0D\u4EC5\u5982\u6B64\uFF0C\u56E0\u4E3A\u6709\u4E00\u4E9B\u4E1A\u52A1\u903B\u8F91\u5728<code>View</code>\u91CC\u5B9E\u73B0\u4E86\uFF0C\u5BFC\u81F4\u8981\u66F4\u6539<code>View</code>\u4E5F\u662F\u6BD4\u8F83\u56F0\u96BE\u7684\uFF0C\u81F3\u5C11\u90A3\u4E9B\u4E1A\u52A1\u903B\u8F91\u662F\u65E0\u6CD5\u91CD\u7528\u7684\u3002</p><p><img src="https://github.com/CharonChui/Pictures/blob/master/is-activity-god-the-mvp-architecture-10-638.jpg?raw=true" alt="image"></p><p>\u5728<code>MVP</code>\u91CC\uFF0C<code>Presenter</code>\u5B8C\u5168\u628A<code>Model</code>\u548C<code>View</code>\u8FDB\u884C\u4E86\u5206\u79BB\uFF0C\u4E3B\u8981\u7684\u7A0B\u5E8F\u903B\u8F91\u5728<code>Presenter</code>\u91CC\u5B9E\u73B0\u3002\u800C\u4E14<code>Presenter</code>\u4E0E\u5177\u4F53\u7684<code>View</code>\u662F\u6CA1\u6709\u76F4\u63A5\u5173\u8054\u7684\uFF0C \u800C\u662F\u901A\u8FC7\u5B9A\u4E49\u597D\u7684\u63A5\u53E3\u8FDB\u884C\u4EA4\u4E92\uFF0C\u4ECE\u800C\u4F7F\u5F97\u5728\u53D8\u66F4<code>View</code>\u65F6\u5019\u53EF\u4EE5\u4FDD\u6301<code>Presenter</code>\u7684\u4E0D\u53D8\uFF0C\u5373\u91CD\u7528\uFF01 \u5728MVP\u91CC\uFF0C\u5E94\u7528\u7A0B\u5E8F\u7684\u903B\u8F91\u4E3B\u8981\u5728<code>Presenter</code>\u6765\u5B9E\u73B0\uFF0C\u5176\u4E2D\u7684<code>View</code>\u662F\u5F88\u8584\u7684\u4E00\u5C42\u3002\u56E0\u6B64\u5C31\u6709\u4EBA\u63D0\u51FA\u4E86<code>Presenter First</code>\u7684\u8BBE\u8BA1\u6A21\u5F0F\uFF0C \u5C31\u662F\u6839\u636E<code>User Story</code>\u6765\u9996\u5148\u8BBE\u8BA1\u548C\u5F00\u53D1<code>Presenter</code>\u3002\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\uFF0C<code>View</code>\u662F\u5F88\u7B80\u5355\u7684\uFF0C\u80FD\u591F\u628A\u4FE1\u606F\u663E\u793A\u6E05\u695A\u5C31\u53EF\u4EE5\u4E86\u3002 \u5728\u540E\u9762\uFF0C\u6839\u636E\u9700\u8981\u518D\u968F\u4FBF\u66F4\u6539<code>View</code>\uFF0C\u800C\u5BF9<code>Presenter</code>\u6CA1\u6709\u4EFB\u4F55\u7684\u5F71\u54CD\u4E86\u3002 \u5982\u679C\u8981\u5B9E\u73B0\u7684<code>UI</code>\u6BD4\u8F83\u590D\u6742\uFF0C\u800C\u4E14\u76F8\u5173\u7684\u663E\u793A\u903B\u8F91\u8FD8\u8DDF<code>Model</code>\u6709\u5173\u7CFB\uFF0C \u5C31\u53EF\u4EE5\u5728<code>View</code>\u548C<code>Presenter</code>\u4E4B\u95F4\u653E\u7F6E\u4E00\u4E2A<code>Adapter</code>\u3002\u7531\u8FD9\u4E2A<code>Adapter</code>\u6765\u8BBF\u95EE<code>Model</code>\u548C<code>View</code>\uFF0C\u907F\u514D\u4E24\u8005\u4E4B\u95F4\u7684\u5173\u8054\u3002 \u800C\u540C\u65F6\uFF0C\u56E0\u4E3A<code>Adapter</code>\u5B9E\u73B0\u4E86<code>View</code>\u7684\u63A5\u53E3\uFF0C\u4ECE\u800C\u53EF\u4EE5\u4FDD\u8BC1\u4E0E<code>Presenter</code>\u4E4B\u95F4\u63A5\u53E3\u7684\u4E0D\u53D8\u3002\u8FD9\u6837\u5C31\u53EF\u4EE5\u4FDD\u8BC1<code>View</code>\u548C<code>Presenter</code>\u4E4B\u95F4\u63A5\u53E3\u7684\u7B80\u6D01\uFF0C \u53C8\u4E0D\u5931\u53BB<code>UI</code>\u7684\u7075\u6D3B\u6027\u3002\u5728<code>MVP</code>\u6A21\u5F0F\u91CC\uFF0C<code>View</code>\u53EA\u5E94\u8BE5\u6709\u7B80\u5355\u7684<code>Set/Get</code>\u7684\u65B9\u6CD5\uFF0C\u7528\u6237\u8F93\u5165\u548C\u8BBE\u7F6E\u754C\u9762\u663E\u793A\u7684\u5185\u5BB9\uFF0C\u9664\u6B64\u5C31\u4E0D\u5E94\u8BE5\u6709\u66F4\u591A\u7684\u5185\u5BB9\uFF0C \u7EDD\u4E0D\u5BB9\u8BB8\u76F4\u63A5\u8BBF\u95EE<code>Model</code>\u8FD9\u5C31\u662F\u4E0E<code>MVC</code>\u5F88\u5927\u7684\u4E0D\u540C\u4E4B\u5904\u3002</p><ul><li><p>\u4F18\u70B9</p><ul><li>\u6A21\u578B\u4E0E\u89C6\u56FE\u5B8C\u5168\u5206\u79BB\uFF0C\u6211\u4EEC\u53EF\u4EE5\u4FEE\u6539\u89C6\u56FE\u800C\u4E0D\u5F71\u54CD\u6A21\u578B</li><li>\u53EF\u4EE5\u66F4\u9AD8\u6548\u5730\u4F7F\u7528\u6A21\u578B\uFF0C\u56E0\u4E3A\u6240\u6709\u7684\u4EA4\u4E92\u90FD\u53D1\u751F\u5728\u4E00\u4E2A\u5730\u65B9\u2014\u2014<code>Presenter</code>\u5185\u90E8</li><li>\u6211\u4EEC\u53EF\u4EE5\u5C06\u4E00\u4E2A<code>Presenter</code>\u7528\u4E8E\u591A\u4E2A\u89C6\u56FE\uFF0C\u800C\u4E0D\u9700\u8981\u6539\u53D8<code>Presenter</code>\u7684\u903B\u8F91\u3002\u8FD9\u4E2A\u7279\u6027\u975E\u5E38\u7684\u6709\u7528\uFF0C\u56E0\u4E3A\u89C6\u56FE\u7684\u53D8\u5316\u603B\u662F\u6BD4\u6A21\u578B\u7684\u53D8\u5316\u9891\u7E41\u3002</li><li>\u5982\u679C\u6211\u4EEC\u628A\u903B\u8F91\u653E\u5728<code>Presenter</code>\u4E2D\uFF0C\u90A3\u4E48\u6211\u4EEC\u5C31\u53EF\u4EE5\u8131\u79BB\u7528\u6237\u63A5\u53E3\u6765\u6D4B\u8BD5\u8FD9\u4E9B\u903B\u8F91\uFF08\u5355\u5143\u6D4B\u8BD5\uFF09</li></ul></li><li><p>\u7F3A\u70B9 \u7531\u4E8E\u5BF9\u89C6\u56FE\u7684\u6E32\u67D3\u653E\u5728\u4E86<code>Presenter</code>\u4E2D\uFF0C\u6240\u4EE5\u89C6\u56FE\u548C<code>Presenter</code>\u7684\u4EA4\u4E92\u4F1A\u8FC7\u4E8E\u9891\u7E41\u3002\u8FD8\u6709\u4E00\u70B9\u9700\u8981\u660E\u767D\uFF0C\u5982\u679C<code>Presenter</code>\u8FC7\u591A\u5730\u6E32\u67D3\u4E86\u89C6\u56FE\uFF0C \u5F80\u5F80\u4F1A\u4F7F\u5F97\u5B83\u4E0E\u7279\u5B9A\u7684\u89C6\u56FE\u7684\u8054\u7CFB\u8FC7\u4E8E\u7D27\u5BC6\u3002\u4E00\u65E6\u89C6\u56FE\u9700\u8981\u53D8\u66F4\uFF0C\u90A3\u4E48<code>Presenter</code>\u4E5F\u9700\u8981\u53D8\u66F4\u4E86\u3002 \u6BD4\u5982\u8BF4\uFF0C\u539F\u672C\u7528\u6765\u5448\u73B0<code>Html</code>\u7684<code>Presenter</code>\u73B0\u5728\u4E5F\u9700\u8981\u7528\u4E8E\u5448\u73B0Pdf\u4E86\uFF0C\u90A3\u4E48\u89C6\u56FE\u5F88\u6709\u53EF\u80FD\u4E5F\u9700\u8981\u53D8\u66F4\u3002</p></li></ul><h2 id="mvvm" tabindex="-1"><a class="header-anchor" href="#mvvm" aria-hidden="true">#</a> MVVM</h2><p>MVVM\u662FModel-View-ViewModel\u7684\u7B80\u5199\u3002</p><ul><li>\u4F18\u70B9 <code>MVVM</code>\u6A21\u5F0F\u548C<code>MVC</code>\u6A21\u5F0F\u4E00\u6837\uFF0C\u4E3B\u8981\u76EE\u7684\u662F\u5206\u79BB\u89C6\u56FE<code>View</code>\u548C\u6A21\u578B<code>Model</code></li><li>\u4F4E\u8026\u5408\u3002</li><li>\u53EF\u91CD\u7528\u6027\u3002</li><li>\u72EC\u7ACB\u5F00\u53D1\u3002</li><li>\u53EF\u6D4B\u8BD5\u3002</li></ul><hr><ul><li>\u90AE\u7BB1 \uFF1Acharon.chui@gmail.com</li><li>Good Luck!</li></ul>',17),r=[l];function t(V,a){return o(),d("div",null,r)}var M=e(i,[["render",t],["__file","MVC\u4E0EMVP\u53CAMVVM.html.vue"]]);export{M as default};
