import{_ as e,o as d,c as o,e as a}from"./app.e2a3b496.js";const n={},t=a(`<h1 id="\u5C4F\u5E55\u9002\u914D" tabindex="-1"><a class="header-anchor" href="#\u5C4F\u5E55\u9002\u914D" aria-hidden="true">#</a> \u5C4F\u5E55\u9002\u914D</h1><h2 id="\u57FA\u672C\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#\u57FA\u672C\u6982\u5FF5" aria-hidden="true">#</a> \u57FA\u672C\u6982\u5FF5</h2><p><code>Px</code>\u4E0D\u540C\u8BBE\u5907\u663E\u793A\u6548\u679C\u76F8\u540C\u3002\u8FD9\u91CC\u7684\u201C\u76F8\u540C\u201D\u662F\u6307\u50CF\u7D20\u6570\u4E0D\u4F1A\u53D8\uFF0C\u6BD4\u5982\u6307\u5B9A<code>UI</code>\u957F\u5EA6\u662F<code>100px</code>\uFF0C\u90A3\u4E0D\u7BA1\u5206\u8FA8\u7387\u662F\u591A\u5C11<code>UI</code>\u957F\u5EA6\u90FD\u662F<code>100px</code>\u3002 \u4E5F\u6B63\u662F\u56E0\u4E3A\u5982\u6B64\u624D\u9020\u6210\u4E86<code>UI</code>\u5728\u5C0F\u5206\u8FA8\u7387\u8BBE\u5907\u4E0A\u88AB\u653E\u5927\u800C\u5931\u771F\uFF0C\u5728\u5927\u5206\u8FA8\u7387\u4E0A\u88AB\u7F29\u5C0F\u3002</p><h4 id="screen-size-\u5C4F\u5E55\u5C3A\u5BF8" tabindex="-1"><a class="header-anchor" href="#screen-size-\u5C4F\u5E55\u5C3A\u5BF8" aria-hidden="true">#</a> Screen Size\uFF08\u5C4F\u5E55\u5C3A\u5BF8\uFF09</h4><p>\u4E00\u822C\u6240\u8BF4\u7684\u624B\u673A\u5C4F\u5E55\u5927\u5C0F\u59825\u5BF8\u30015.5\u82F1\u5BF8\uFF0C\u90FD\u662F\u6307\u7684\u5C4F\u5E55\u5BF9\u89D2\u7EBF\u7684\u957F\u5EA6\uFF0C\u800C\u4E0D\u662F\u624B\u673A\u9762\u79EF\u3002\u6211\u4EEC\u53EF\u4EE5\u6839\u636E\u52FE\u80A1\u5B9A\u7406\u83B7\u53D6\u624B\u673A\u7684\u5BBD\u548C\u957F\uFF0C\u5F53\u7136\u8FD8\u6709\u9762\u79EF\u3002\u5355\u4F4D\u4E3A<code>inch</code>,1\u82F1\u5BF8=2.54\u5398\u7C73</p><h4 id="resolution-\u5206\u8FA8\u7387" tabindex="-1"><a class="header-anchor" href="#resolution-\u5206\u8FA8\u7387" aria-hidden="true">#</a> Resolution\uFF08\u5206\u8FA8\u7387\uFF09</h4><p>\u6307\u624B\u673A\u5C4F\u5E55\u5782\u76F4\u548C\u6C34\u5E73\u65B9\u5411\u4E0A\u7684\u50CF\u7D20\u4E2A\u6570\u3002\u6BD4\u5982\u5206\u8FA8\u7387\u662F<code>480*320</code>\uFF0C\u5219\u6307\u8BBE\u5907\u5782\u76F4\u65B9\u5411\u6709480\u4E2A\u50CF\u7D20\u70B9\uFF0C\u6C34\u5E73\u65B9\u5411\u6709320\u4E2A\u50CF\u7D20\u70B9\u3002\u5355\u4F4D<code>px * px</code>\uFF0C\u6307\u7684\u662F\u4E00\u5C4F\u663E\u793A\u591A\u5C11\u50CF\u7D20\u70B9\u3002</p><h4 id="dpi-dots-per-inch\u5C4F\u5E55\u5BC6\u5EA6" tabindex="-1"><a class="header-anchor" href="#dpi-dots-per-inch\u5C4F\u5E55\u5BC6\u5EA6" aria-hidden="true">#</a> Dpi\uFF08dots per inch\u5C4F\u5E55\u5BC6\u5EA6\uFF09</h4><p>\u5355\u4F4D<code>dpi</code>\uFF0C\u6307\u7684\u662F\u6BCF<code>inch</code>\u4E0A\u53EF\u4EE5\u663E\u793A\u591A\u5C11\u50CF\u7D20\u70B9\u5373<code>px</code>\u3002(\u6BCF\u82F1\u5BF8\u4E2D\u7684\u50CF\u7D20\u6570)\u5982<code>160dpi</code>\u6307\u624B\u673A\u6C34\u5E73\u6216\u5782\u76F4\u65B9\u5411\u4E0A\u6BCF\u82F1\u5BF8\u8DDD\u79BB\u6709<code>160</code>\u4E2A\u50CF\u7D20\u70B9\u3002<code>dpi</code>\u8D8A\u9AD8\uFF0C\u6BCF\u4E2A\u50CF\u7D20\u70B9\u8D8A\u5C0F\u4E5F\u5C31\u8D8A\u6E05\u6670\u3002 \u7B80\u5355\u5730\u8BF4\uFF0C<code>dpi</code>\u8D8A\u9AD8\u5C31\u610F\u5473\u7740\u6BCF\u82F1\u5BF8\u663E\u793A\u7684\u7EC6\u8282\u8D8A\u591A\uFF0C\u800C\u4E0D\u662F\u76F4\u63A5\u53D6\u51B3\u4E8E\u9AD8\u5206\u8FA8\u3002\u4E3E\u4E2A\u4F8B\u5B50\uFF0C <code>Galaxy Nexus</code>(\u5BF9\u89D2\u7EBF 4.65\u201D)\u5206\u8FA8\u7387\u4E3A\uFF1A<code>720\xD71280</code>,<code>Nexus 7</code>(\u5BF9\u89D2\u7EBF 7\u201D)\u5206\u8FA8\u7387\u4E3A\uFF1A<code>800\xD71280</code>. \u5E38\u89C1\u7684\u9519\u8BEF\u89C2\u70B9\u662F\u8BA4\u4E3A\u4ED6\u4EEC\u62E5\u6709\u76F8\u540C\u7684\u5C4F\u5E55\u50CF\u7D20\u5BC6\u5EA6\uFF0C\u56E0\u4E3A\u5B83\u4EEC\u7684\u5206\u8FA8\u7387\u51E0\u4E4E\u4E00\u6837\u3002 \u7136\u800C\uFF0C<code>Galaxy Nexus</code> \u7684\u5C4F\u5E55\u50CF\u7D20\u5BC6\u5EA6\u7EA6\u4E3A<code>316dpi</code>\uFF0C<code>Nexus 7</code>\u7684\u5C4F\u5E55\u50CF\u7D20\u5BC6\u5EA6\u4E3A<code>216dpi</code>\uFF0C\u76F8\u5DEE\u751A\u8FDC\u3002 \u8FD9\u662F\u56E0\u4E3A\u867D\u7136\u5B83\u4EEC\u62E5\u6709\u4E00\u6837\u7684\u5206\u8FA8\u7387\uFF0C\u4F46\u662F\u5B83\u4EEC\u663E\u793A\u5C3A\u5BF8\u5374\u4E0D\u4E00\u6837\u3002 \u518D\u6B21\u8BF4\u660E\uFF0C\u5C4F\u5E55\u50CF\u7D20\u5BC6\u5EA6\u662F\u5206\u8FA8\u7387\u548C\u663E\u793A\u5C3A\u5BF8\u7684\u6BD4\u503C\uFF0C\u4E24\u4E2A\u56E0\u7D20\u4E00\u8D77\u51B3\u5B9A\u5C4F\u5E55\u50CF\u7D20\u5BC6\u5EA6\u3002</p><p><img src="https://raw.githubusercontent.com/CharonChui/Pictures/master/dpi_calculate.png?raw=true" alt="image"><br> \u4F8B\u5982\uFF1A\u8BA1\u7B97<code>Nexus5</code>\u7684\u5C4F\u5E55\u50CF\u7D20\u5BC6\u5EA6\uFF1A \u5C4F\u5E55\u5C3A\u5BF8\uFF1A<code>4.95inch</code>\u3001\u5206\u8FA8\u7387\uFF1A<code>1920\xD71080</code>\uFF0C\u5C4F\u5E55\u50CF\u7D20\u5BC6\u5EA6\uFF1A<code>445</code><br><img src="https://raw.githubusercontent.com/CharonChui/Pictures/master/dpi_nexus5.png?raw=true" alt="image"></p><h4 id="dip-device-independent-pixel-\u8BBE\u5907\u72EC\u7ACB\u50CF\u7D20" tabindex="-1"><a class="header-anchor" href="#dip-device-independent-pixel-\u8BBE\u5907\u72EC\u7ACB\u50CF\u7D20" aria-hidden="true">#</a> Dip\uFF08Device-independent pixel\uFF0C\u8BBE\u5907\u72EC\u7ACB\u50CF\u7D20\uFF09</h4><p>\u540C<code>dp</code>\uFF0C\u6307\u7684\u662F\u81EA\u9002\u5E94\u5C4F\u5E55\u5BC6\u5EA6\u7684\u50CF\u7D20\uFF0C\u7528\u4E8E\u6307\u5B9A\u63A7\u4EF6\u5BBD\u9AD8\u3002\u5C31\u6240\u5C4F\u5E55\u5BC6\u5EA6\u53D8\u5927\uFF0C1dp\u6240\u5BF9\u5E94\u7684px\u4E5F\u4F1A\u53D8\u5927\u3002 \u4E0D\u540C\u8BBE\u5907\u6709\u4E0D\u540C\u7684\u663E\u793A\u6548\u679C,\u8FD9\u4E2A\u548C\u8BBE\u5907\u786C\u4EF6\u6709\u5173\uFF0C\u4E00\u822C\u6211\u4EEC\u4E3A\u4E86\u652F\u6301<code>WVGA\u3001HVGA</code>\u548C<code>QVGA</code>\u63A8\u8350\u4F7F\u7528\u8FD9\u4E2A\uFF0C \u4E0D\u4F9D\u8D56\u50CF\u7D20\u3002 <code>dip</code>\u548C\u5177\u4F53\u50CF\u7D20\u503C\u7684\u5BF9\u5E94\u516C\u5F0F\u662F<code>dip\u503C =\u8BBE\u5907\u5BC6\u5EA6/160* pixel</code>\u503C\uFF0C\u53EF\u4EE5\u770B\u51FA\u5728<code>dpi</code>\uFF08\u50CF\u7D20\u5BC6\u5EA6\uFF09\u4E3A<code>160dpi</code>\u7684\u8BBE\u5907\u4E0A<code>1px=1dip</code>, \u5728<code>Android</code>\u4E2D\uFF0C\u89C4\u5B9A\u4EE5<code>160dpi</code>\u4E3A\u57FA\u51C6\uFF0C<code>1dip=1px</code>\uFF0C\u5982\u679C\u5BC6\u5EA6\u662F<code>320dpi</code>\uFF0C\u5219<code>1dip=2px</code>\uFF0C \u4EE5\u6B64\u7C7B\u63A8\u3002</p><h4 id="sp-scaledpixels\u653E\u5927\u50CF\u7D20" tabindex="-1"><a class="header-anchor" href="#sp-scaledpixels\u653E\u5927\u50CF\u7D20" aria-hidden="true">#</a> Sp\uFF08ScaledPixels\u653E\u5927\u50CF\u7D20\uFF09</h4><p>\u4E3B\u8981\u7528\u4E8E\u5B57\u4F53\u663E\u793A<code>\uFF08best for textsize\uFF09</code>\u3002\u6839\u636E<code>google</code>\u7684\u5EFA\u8BAE\uFF0C<code>TextView</code>\u7684\u5B57\u53F7\u6700\u597D\u4F7F\u7528<code>sp</code>\u505A\u5355\u4F4D\uFF0C \u800C\u4E14\u67E5\u770B<code>TextView</code>\u7684\u6E90\u7801\u53EF\u77E5<code>Android</code>\u9ED8\u8BA4\u4F7F\u7528<code>sp</code>\u4F5C\u4E3A\u5B57\u53F7\u5355\u4F4D\u3002<code>Google</code>\u63A8\u8350\u6211\u4EEC\u4F7F\u7528<code>12sp</code>\u4EE5\u4E0A\u7684\u5927\u5C0F\uFF0C \u901A\u5E38\u53EF\u4EE5\u4F7F\u7528<code>12sp</code>\uFF0C<code>14sp</code>\uFF0C<code>18sp</code>\uFF0C<code>22sp</code>\uFF0C\u6700\u597D\u4E0D\u8981\u4F7F\u7528\u5947\u6570\u548C\u5C0F\u6570\u3002</p><p>\u6211\u4EEC\u53EF\u4EE5\u7528\u4E0B\u9762\u7684\u90E8\u5206\u6765\u89E3\u91CA\u4E3A\u4EC0\u4E48\u7528dip\u4EE3\u66FFpx\u4F5C\u5355\u4F4D\uFF1A<br> \u8BBE\u5907\u6700\u7EC8\u4F1A\u4EE5<code>px</code>\u4F5C\u4E3A\u957F\u5EA6\u5355\u4F4D\u3002<br> \u5982\u679C\u6211\u4EEC\u76F4\u63A5\u7528<code>px</code>\u4F5C\u4E3A\u5355\u4F4D\u4F1A\u9020\u6210<code>UI</code>\u5728\u4E0D\u540C\u5206\u8FA8\u7387\u8BBE\u5907\u4E0A\u51FA\u73B0\u4E0D\u5408\u9002\u7684\u7F29\u653E\u3002 \u56E0\u6B64\u6211\u4EEC\u9700\u8981\u4E00\u79CD\u65B0\u7684\u5355\u4F4D\uFF0C\u8FD9\u79CD\u5355\u4F4D\u8981\u6700\u7EC8\u80FD\u591F\u4EE5\u5408\u9002\u7684\u7CFB\u6570\u6362\u7B97\u6210<code>px</code>\u4F7F<code>UI</code>\u8868\u73B0\u51FA\u5408\u9002\u7684\u5927\u5C0F\u3002<br><code>Dip</code>\u7B26\u5408\u8FD9\u79CD\u8981\u6C42\u5417\uFF1F<br> \u7531<code>dip</code>\u548C\u5177\u4F53\u50CF\u7D20\u503C\u7684\u5BF9\u5E94\u516C\u5F0F<code>dip\u503C =\u8BBE\u5907\u5BC6\u5EA6/160* pixel</code>\u503C\u53EF\u4EE5\u77E5 \u4E5F\u5C31\u662F\u8BF4<code>UI</code>\u6700\u7EC8\u7684pixel\u503C\u53EA\u53D7\u50CF\u7D20\u5BC6\u5EA6dip\u7684\u5F71\u54CD\uFF0C \u8FD9\u4E2A<code>dip</code>\u5C31\u76F8\u5F53\u4E8E\u90A3\u4E2A\u6362\u7B97\u7CFB\u6570\uFF0C\u8FD9\u4E2A\u7CFB\u6570\u7684\u503C\u662F\u591A\u5C11\u6709\u8BBE\u5907\u5546\u53BB\u51B3\u5B9A\u3002\u56E0\u6B64<code>dip</code>\u7B26\u5408\u8FD9\u79CD\u8981\u6C42\u3002</p><p>##android\u4E3A\u4EC0\u4E48\u5F15\u8FDBdip</p><blockquote><p>The reason for dip to exist is simple enough. Take for instance the T-Mobile G1. It has a pixel resolution of 320x480 pixels. Now image another device, with the same physical screen size, but more pixels, for instance 640x480. This device would have a higher pixel density than the G1.</p></blockquote><blockquote><p>\u2014If you specify, in your application, a button with a width of 100 pixels, it will look at lot smaller on the 640x480 device than on the 320x480 device. Now, if you specify the width of the button to be 100 dip, the button will appear to have exactly the same size on the two devices.</p></blockquote><blockquote><p>\u2014The density-independent pixel is equivalent to one physical pixel on a 160 dpi screen, the baseline density assumed by the platform (as described later in this document). At run time, the platform transparently handles any scaling of the dip units needed, based on the actual density of the screen in use. The conversion of dip units to screen pixels is simple: pixels = dips * (density / 160). For example, on 240 dpi screen, 1 dip would equal 1.5 physical pixels.Using dip units to define your application&#39;s UI is highly recommended, as a way of ensuring proper display of your UI on different screens.</p></blockquote><p><code>dip</code>\u662F\u4E00\u79CD\u80FD\u81EA\u9002\u5E94\u5C4F\u5E55\u5BC6\u5EA6\u7684\u50CF\u7D20\uFF0C\u8FD9\u4E2A\u5C4F\u5E55\u5BC6\u5EA6\u5C31\u662F\u5C4F\u5E55\u4E2D\u4E00\u82F1\u5BF8\u91CC\u9762\u542B\u6709\u7684\u50CF\u7D20\u6570\uFF0C\u6253\u4E2A\u6BD4\u65B9\u8BF4\uFF0C\u73B0\u5728\u6709\u4E24\u4E2A4\u5BF8\u7684\u624B\u673A\uFF0C\u4E00\u4E2A\u5206\u8FA8\u7387\u662F<code>320*480</code>, \u5B83\u7684\u5C4F\u5E55\u5BC6\u5EA6\u662F1\uFF0C \u800C\u53E6\u4E00\u4E2A\u662F<code>480*800</code>\u7684\u5206\u8FA8\u7387,\u8FD9\u6837\u4ED6\u7684\u5C4F\u5E55\u5BC6\u5EA6\u5C31\u662F1.5\uFF0C\u5982\u679C\u6211\u4EEC\u5728320<em>480\u7684\u624B\u673A\u4E2D\u5F04\u4E86\u4E00\u4E2A160dp\u7684\u6A2A\u7EBF\uFF0C \u7531\u4E8E\u4ED6\u7684\u5C4F\u5E55\u5BC6\u5EA6\u662F1\uFF0C\u6240\u4EE5\u4ED6\u91CC\u9762dp\u548Cpx\u662F\u4E00\u6BD4\u4E00\u7684\u8BA1\u7B97\u3002 \u8FD9\u6837\u8FD9\u6761\u6A2A\u7EBF\u6B63\u597D\u5360\u5C4F\u5E55\u7684\u4E00\u534A\uFF0C\u800C\u6211\u4EEC\u5C06\u8FD9\u4E2A\u6A2A\u7EBF\u7684\u89C6\u56FE\u53D1\u5E03\u5230480</em>800\u7684\u624B\u673A\u4E0A\u65F6\uFF0C \u867D\u7136\u4E5F\u662F160dp\uFF0C \u4F46\u662F\u7531\u4E8E\u4ED6\u7684\u5C4F\u5E55\u5BC6\u5EA6\u662F1.5.\u6309\u7167dp\u4E0Epx\u7684\u8BA1\u7B97\u516C\u5F0Fpixel\u503C=dip\u503C/(\u8BBE\u5907\u5BC6\u5EA6/160)\uFF0C \u53EF\u4EE5\u5F97\u5230\u5C06160dp\u8981\u4E58\u4EE51.5\u500D\uFF0C\u8FD9\u6837\u5F97\u5230\u7684px\u5C31\u662F240px\uFF0C \u800C\u8FD9\u4E2A\u624B\u673A\u7684\u5206\u8FA8\u7387\u6B63\u597D\u662F480*800\uFF0C\u6240\u4EE5\u770B\u8D77\u6765\u4ECD\u7136\u662F\u5360\u5C4F\u5E55\u7684\u4E00\u534A\u3002 \u6CE8\u610F\uFF1A\u4E0D\u8BBA\u591A\u5C11<code>dp</code>\u6700\u540E\u90FD\u662F\u8981\u901A\u8FC7\u8F6C\u6362\u6210<code>px</code>\u6765\u663E\u793A\u7684\u624B\u673A\u7684\u5C4F\u5E55\u4E0A\u7684\uFF0C \u81F3\u4E8E\u8FD9\u4E2A<code>dp</code>\u7684\u5927\u5C0F\u5728\u5C4F\u5E55\u4E0A\u5230\u5E95\u80FD\u5360\u591A\u5C11\u4F4D\u7F6E\uFF0C\u5C31\u8981\u770B\u8FD9\u4E2A\u8F6C\u6362\u540E\u7684<code>px</code>\u4E0E\u5C4F\u5E55\u7684\u5BBD\u5EA6\u50CF\u7D20\u7684\u6BD4\u503C\u4E86\u3002</p><p>\u53E6\u4E00\u4E2A\u4F8B\u5B50\uFF1A \u6253\u4E2A\u6BD4\u65B9\u4E00\u4E2A\u662F4\u5BF8<code>480*800</code>\u7684\u624B\u673A\uFF0C\u5C4F\u5E55\u5BC6\u5EA6\u4E3A1.5. \u53E6\u4E00\u4E2A\u662F7\u5BF8\u7684<code>1280*800</code>\u7684\u5E73\u677F\u3002\u5C4F\u5E55\u5BC6\u5EA6\u4E5F\u4E3A1.<br> \u8FD9\u6837\u7528160dp\u7684\u6A2A\u7EBF\uFF0C\u5728<code>480*800</code>\u7684\u624B\u673A\u4E0A\u6362\u7B97\u6210<code>px</code>\u662F<code>160*1.5 = 240</code>\uFF0C\u8FD9\u6837\u6A2A\u5411\u6B63\u597D\u5360\u5C4F\u5E55\u7684\u4E00\u534A\uFF0C \u4F46\u662F\u5728<code>1280*800</code>\u7684\u5E73\u677F\u4E0A\uFF0C<code>160dp</code>\u6362\u7B97\u540E\u7684<code>px</code>\u4E3A\uFF0C<code>160*1 = 160px</code>, \u800C\u5C4F\u5E55\u7684\u5BBD\u662F<code>1280</code>.\u8FD9\u6837\u8FD9\u6761\u6A2A\u7EBF\u663E\u793A\u5728<code>1280*800</code>\u7684\u5E73\u677F\u4E0A\u540E\uFF0C\u662F<code>160/1280</code>\u6B63\u597D\u662F\u516B\u5206\u4E4B\u4E00\u3002</p><p>\u6240\u4EE5\u5F15\u5165dp\u89E3\u51B3\u7684\u662F\u4EC0\u4E48\u95EE\u9898\u5462\uFF1F\u540C\u4E00\u4E2A\u5C4F\u5E55\u5C3A\u5BF8\u4E0B\uFF0C\u5BF9\u4E8E\u4E0D\u540C\u7684\u5206\u8FA8\u7387\uFF0C\u7531\u4E8E\u5C3A\u5BF8\u76F8\u540C\uFF0C\u5206\u8FA8\u7387\u4E0D\u540C\uFF0C\u6240\u4EE5\u5B83\u7684\u5C4F\u5E55\u5BC6\u5EA6\u5C31\u4F1A\u4E0D\u4E00\u6837\uFF0C \u8FD9\u6837\u5728\u540C\u4E00\u5C3A\u5BF8\u7684\u4E0D\u540C\u5206\u8FA8\u7387\u4E0B\u7684\u8BBE\u5907\u4E0A\uFF0C\u663E\u793A\u51FA\u6765\u4F1A\u770B\u5230\u6240\u5360\u7684\u6BD4\u4F8B\u57FA\u672C\u4E00\u6837\u3002\u5982\u4E24\u4E2A7\u5BF8\u7684\u5E73\u677F\uFF0C\u4E00\u4E2A\u662F<code>1280*800</code>\uFF0C\u4E00\u4E2A\u662F<code>2560*1600</code>\uFF0C \u8FD9\u6837\u7528<code>dp</code>\u540E\uFF0C\u867D\u7136<code>2560*1600</code>\u7684\u5206\u8FA8\u7387\u6BD4\u90A3\u4E2A\u8981\u5927\u4E00\u500D\uFF0C\u4F46\u662F\u5B83\u5C3A\u5BF8\u4E00\u6837\u554A\uFF0C \u6240\u4EE5\u5C4F\u5E55\u7684\u5206\u8FA8\u7387\u4F1A\u662F\u53E6\u4E00\u4E2A\u76842\u500D\uFF0C\u8FD9\u6837dp\u8F6C\u6210px\u540E\u4F1A\u4E58\u4EE52.\u8FD9\u6837\u663E\u793A\u5230\u8FD9\u4E24\u4E2A\u8BBE\u5907\u4E0A\u6240\u5360\u7684\u6BD4\u4F8B\u4F1A\u4E00\u6837</p><p>\u4E00\u4E2A\u662F<code>7</code>\u5BF8<code>1280*800</code>\u7684\u624B\u673A\uFF0C\u5BC6\u5EA6\u4E3A1\uFF0C\u4E00\u4E2A\u662F5\u5BF8<code>1024*600\u7684</code>\u624B\u673A\uFF0C\u5BC6\u5EA6\u4E3A1. \u8FD9\u6837\u4F60\u7528160dp\u7684\u7EBF\uFF0C\u5728\u524D\u9762\u662F8\u5206\u4E4B\u4E00\uFF0C\u5728\u540E\u9762\u662F\u516D\u5206\u4E4B\u4E00\uFF0C\u4F60\u5C31\u60B2\u5267\u4E86\uFF0C\u6240\u4EE5\u8981\u505A\u5C4F\u5E55\u9002\u914D\u4E86</p><h2 id="\u4EE3\u7801" tabindex="-1"><a class="header-anchor" href="#\u4EE3\u7801" aria-hidden="true">#</a> \u4EE3\u7801</h2><div class="language-java ext-java line-numbers-mode"><pre class="language-java"><code><span class="token class-name">DisplayMetrics</span> displayMetrics <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">DisplayMetrics</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token function">getWindowManager</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">getDefaultDisplay</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">getMetrics</span><span class="token punctuation">(</span>displayMetrics<span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token class-name">Log</span><span class="token punctuation">.</span><span class="token function">e</span><span class="token punctuation">(</span><span class="token string">&quot;@@@&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;density:&quot;</span><span class="token operator">+</span>displayMetrics<span class="token punctuation">.</span>density<span class="token punctuation">)</span><span class="token punctuation">;</span>    <span class="token comment">//1</span>
<span class="token class-name">Log</span><span class="token punctuation">.</span><span class="token function">e</span><span class="token punctuation">(</span><span class="token string">&quot;@@@&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;densityDpi:&quot;</span><span class="token operator">+</span>displayMetrics<span class="token punctuation">.</span>densityDpi<span class="token punctuation">)</span><span class="token punctuation">;</span>   <span class="token comment">//160</span>
<span class="token class-name">Log</span><span class="token punctuation">.</span><span class="token function">e</span><span class="token punctuation">(</span><span class="token string">&quot;@@@&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;widthPixels:&quot;</span><span class="token operator">+</span>displayMetrics<span class="token punctuation">.</span>widthPixels<span class="token punctuation">)</span><span class="token punctuation">;</span>   <span class="token comment">//1024</span>
<span class="token class-name">Log</span><span class="token punctuation">.</span><span class="token function">e</span><span class="token punctuation">(</span><span class="token string">&quot;@@@&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;heightPixels:&quot;</span><span class="token operator">+</span>displayMetrics<span class="token punctuation">.</span>heightPixels<span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">//600</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p><code>displayMetrics.densityDpi</code>\u662F\u771F\u6B63\u7684\u5C4F\u5E55\u5BC6\u5EA6\uFF0C\u53EF\u4EE5\u4E3A<code>160dpi</code>(<code>mdpi</code>\u6807\u51C6\u7684\u5C4F\u5E55\u5BC6\u5EA6),<code>240dpi</code>(<code>hdpi</code>), <code>320dpi(xhdpi)</code>. <code>displayMetrics.density</code>,\u8FD9\u4E2A\u4E0D\u53EB\u5C4F\u5E55\u5BC6\u5EA6\uFF0C\u4F46\u662F\u5728<code>dp</code>\u548C<code>px</code>\u8F6C\u6362\u7684\u65F6\u5019\u8981\u4F7F\u7528\u5230\uFF0C \u8FD9\u4E2A\u662F\u5C4F\u5E55\u5BC6\u5EA6\u7684\u6BD4\u4F8B\u503C\uFF0C\u662F\u7528\u8BE5\u8BBE\u5907\u7684\u5C4F\u5E55\u5BC6\u5EA6\u9664\u4EE5\u6807\u51C6\u7684\u5C4F\u5E55\u5BC6\u5EA6\u5F97\u5230\u7684\u4E00\u4E2A\u6BD4\u503C\uFF0C \u5982<code>240dpi/160dpi = 1.5</code></p><h2 id="\u5C4F\u5E55\u9002\u914D\u6587\u4EF6\u5939\u547D\u540D\u89C4\u5219" tabindex="-1"><a class="header-anchor" href="#\u5C4F\u5E55\u9002\u914D\u6587\u4EF6\u5939\u547D\u540D\u89C4\u5219" aria-hidden="true">#</a> \u5C4F\u5E55\u9002\u914D\u6587\u4EF6\u5939\u547D\u540D\u89C4\u5219</h2><ul><li><p>\u547D\u540D\u4E0D\u533A\u5206\u5927\u5C0F\u5199\uFF1B</p></li><li><p>\u547D\u540D\u5F62\u5F0F\uFF1A\u8D44\u6E90\u540D-\u5C5E\u60271-\u5C5E\u60272-\u5C5E\u60273-\u5C5E\u60274-\u5C5E\u60275..... \u8D44\u6E90\u540D\u5C31\u662F\u8D44\u6E90\u7C7B\u578B\u540D\uFF0C\u5305\u62EC:drawable, values, layout, anim, raw, menu, color, animator, xml\uFF1B \u5C5E\u60271-\u5C5E\u60272-\u5C5E\u60273-\u5C5E\u60274-\u5C5E\u60275.....\u5C31\u662F\u4E0A\u8FF0\u7684\u5C5E\u6027\u96C6\u5185\u7684\u5C5E\u6027\uFF0C\u5982:-en-port-hdpi\uFF1B \u6CE8\u610F\uFF1A\u5404\u5C5E\u6027\u7684\u4F4D\u7F6E\u987A\u5E8F\u5FC5\u987B\u9075\u5B88\u4F18\u5148\u7EA7\u4ECE\u9AD8\u5230\u4F4E\u6392\u5217\uFF01\u5426\u5219\u7F16\u8BD1\u4E0D\u8FC7</p></li><li><p>\u5B9E\u4F8B\u8BF4\u660E</p><ul><li>\u628A\u5168\u90E8\u5C5E\u6027\u90FD\u7528\u4E0A\u7684\u4F8B\u5B50(\u5404\u5C5E\u6027\u662F\u6309\u4F18\u5148\u7EA7\u5148\u540E\u6392\u5217\u51FA\u6765\u7684)<br> values-mcc310-en-sw320dp-w720dp-h720dp-large-long-port-car-night-ldpi-notouch-keysexposed-nokeys-navexposed-nonav-v7</li><li>\u4E0A\u8FF0\u4F8B\u5B50\u5C5E\u6027\u7684\u4E2D\u6587\u8BF4\u660E<br> values-mcc310(sim\u5361\u8FD0\u8425\u5546)-en(\u8BED\u8A00)-sw320dp(\u5C4F\u5E55\u6700\u5C0F\u5BBD\u5EA6)-w720dp(\u5C4F\u5E55\u6700\u4F73\u5BBD\u5EA6)-h720dp(\u5C4F\u5E55\u6700\u4F73\u9AD8\u5EA6)-large(\u5C4F\u5E55\u5C3A\u5BF8)-long(\u5C4F\u5E55\u957F\u77ED\u8FB9\u6A21\u5F0F) -port(\u5F53\u524D\u5C4F\u5E55\u6A2A\u7AD6\u5C4F\u663E\u793A\u6A21\u5F0F)-car(dock\u6A21\u5F0F)-night(\u767D\u5929\u6216\u591C\u665A)-ldpi(\u5C4F\u5E55\u6700\u4F73dpi)-notouch(\u89E6\u6478\u5C4F\u6A21\u7C7B\u578B)-keysexposed(\u952E\u76D8\u7C7B\u578B)-nokey(\u786C\u6309\u952E\u7C7B\u578B) -navexposed(\u65B9\u5411\u952E\u662F\u5426\u53EF\u7528)-nonav(\u65B9\u5411\u952E\u7C7B\u578B)-v7(android\u7248\u672C)</li></ul></li></ul><h2 id="\u624B\u673A\u5E38\u89C1\u5206\u8FA8\u7387" tabindex="-1"><a class="header-anchor" href="#\u624B\u673A\u5E38\u89C1\u5206\u8FA8\u7387" aria-hidden="true">#</a> \u624B\u673A\u5E38\u89C1\u5206\u8FA8\u7387</h2><p>4:3<br> VGA 640<em>480 (Video Graphics Array)<br> QVGA 320</em>240 (Quarter VGA)<br> HVGA 480<em>320 (Half-size VGA)<br> SVGA 800</em>600 (Super VGA)</p><p>5:3<br> WVGA 800*480 (Wide VGA)</p><p>16:9<br> FWVGA 854<em>480 (Full Wide VGA)<br> HD 1920</em>1080 High Definition<br> QHD 960<em>540<br> 720p 1280</em>720 \u6807\u6E05<br> 1080p 1920*1080 \u9AD8\u6E05</p><table><thead><tr><th>\u5206\u8FA8\u7387\u5BF9\u5E94DPI</th><th style="text-align:center;">\u50CF\u7D20\u5BC6</th><th style="text-align:center;">\u901A\u5E38\u7684\u5206\u8FA8\u7387</th><th style="text-align:right;">\u6BD4\u4F8B\u5927\u5C0F</th></tr></thead><tbody><tr><td>HVGA</td><td style="text-align:center;">mdpi</td><td style="text-align:center;">160dpi</td><td style="text-align:right;">320*480</td></tr><tr><td>WVGA</td><td style="text-align:center;">hdpi</td><td style="text-align:center;">240dpi</td><td style="text-align:right;">480*800</td></tr><tr><td>720P</td><td style="text-align:center;">xhdpi</td><td style="text-align:center;">320dpi</td><td style="text-align:right;">720*1280</td></tr><tr><td>1080P</td><td style="text-align:center;">xxhdpi</td><td style="text-align:center;">480dpi</td><td style="text-align:right;">1080*1920</td></tr><tr><td>xx</td><td style="text-align:center;">xxxhdpi</td><td style="text-align:center;">640dpi</td><td style="text-align:right;"></td></tr></tbody></table><p><img src="https://raw.githubusercontent.com/CharonChui/Pictures/master/xhdpi.jpg?raw=true" alt="image"></p><p><img src="https://raw.githubusercontent.com/CharonChui/Pictures/master/icon_size.jpg?raw=true" alt="image"></p><p>\u4E3A\u4E86\u89E3\u51B3\u9002\u914D\u7684\u95EE\u9898\u6709\u65F6\u5019\u8FD8\u4F1A\u4F7F\u75289patch\u56FE\uFF0C\u8FD9\u91CC\u968F\u4FBF\u8BF4\u4E00\u4E0B\u3002</p><p><img src="https://raw.githubusercontent.com/CharonChui/Pictures/master/9patch.jpg?raw=true" alt="image"></p><ul><li><code>Stretchable area</code>\u4EE3\u8868\u62C9\u4F38\u7684\u90E8\u5206\u3002</li><li><code>Padding box</code>\u4EE3\u7801\u5185\u5BB9\u6240\u5728\u533A\u57DF\u3002\u5916\u9762\u7684\u5C31\u662F<code>padding</code>\u90E8\u5206\u3002</li></ul><p><img src="https://raw.githubusercontent.com/CharonChui/Pictures/master/dpi_drawable.png?raw=true" alt="image"></p><h3 id="android\u56FE\u7247\u9009\u62E9\u7B56\u7565" tabindex="-1"><a class="header-anchor" href="#android\u56FE\u7247\u9009\u62E9\u7B56\u7565" aria-hidden="true">#</a> Android\u56FE\u7247\u9009\u62E9\u7B56\u7565</h3><p>\u4E0A\u9762\u8BF4\u5230\uFF0C\u5982\u679C\u5C4F\u5E55\u6240\u5BF9\u5E94\u7684\u6587\u4EF6\u5939\u6CA1\u6709\u8981\u627E\u7684\u56FE\u7247\u600E\u4E48\u529E\u3002 \u8FD9\u662F\u5F88\u5E38\u89C1\u7684\uFF0C\u6211\u4EEC\u5F00\u53D1\u9879\u76EE\u65F6\u4E00\u822C\u4E0D\u4F1A\u53BB\u4E3A\u6BCF\u4E00\u4E2A\u7EA7\u522B\u7684\u5C4F\u5E55\u53BB\u5207\u4E00\u5957\u56FE\u7247\u3002 \u90A3\u6837\u505A\u53EA\u4F1A\u8BA9<code>apk</code>\u5F88\u5927\u3002\u6240\u4EE5\u4E00\u822C\u6027\u7684\u56FE\u7247\u6211\u4EEC\u53EA\u5207\u4E00\u4E24\u4E2A\u5178\u578B\u5BC6\u5EA6\u5C4F\u5E55\u7684\u56FE\u7247\u3002 \u4F46\u662F<code>apk</code>\u662F\u6709\u53EF\u80FD\u4F1A\u8FD0\u884C\u5728\u4ECE<code>ldpi</code>\u5230<code>xxhdpi</code>\u7684\u5404\u79CD\u7EA7\u522B\u7684\u624B\u673A\u4E0A\u3002 \u8FD9\u4E2A\u65F6\u5019\u5C31\u9700\u8981\u6839\u636E\u4E00\u5B9A\u7684\u7B56\u7565\u53BB\u5BFB\u627E\u56FE\u7247\u4E86\u3002</p><p><code>Android</code>\u7CFB\u7EDF\u5BFB\u627E\u56FE\u7247\u7684\u6B65\u9AA4\u662F\u8FD9\u6837\u7684\uFF1A</p><ul><li>\u53BB\u5C4F\u5E55\u5BC6\u5EA6\u5BF9\u5E94\u7684\u76EE\u5F55\u53BB\u627E\u3002\u5982\u679C\u627E\u5230\u5C31\u62FF\u6765\u7528\u3002</li><li>\u5982\u679C\u6CA1\u627E\u5230\uFF0C\u5C31\u53BB\u6BD4\u8FD9\u4E2A\u5BC6\u5EA6\u9AD8\u4E00\u7EA7\u7684\u76EE\u5F55\u91CC\u9762\u53BB\u627E\uFF0C\u5982\u679C\u627E\u5230\u5C31\u62FF\u6765\u7528\u3002</li><li>\u5982\u679C\u6CA1\u627E\u5230\u5C31\u7EE7\u7EED\u5F80\u4E0A\u627E\u3002\u4EE5\u6B64\u7C7B\u63A8\u3002</li><li>\u5982\u679C\u5230\u4E86xxhdpi\u76EE\u5F55\u8FD8\u6CA1\u6709\u627E\u5230\u7684\u8BDD\uFF0C\u5C31\u4F1A\u53BB\u6BD4\u81EA\u8EAB\u5C4F\u5E55\u5BC6\u5EA6\u4F4E\u4E00\u7EA7\u7684\u76EE\u5F55\u53BB\u627E\uFF0C\u5982\u679C\u4F4E\u4E00\u7EA7\u7684\u76EE\u5F55&gt;=hdpi\uFF0C\u627E\u5230\u4E86\u5C31\u62FF\u6765\u7528\u3002</li><li>\u5982\u679C\u6CA1\u627E\u5230\uFF0C \u5C31\u53BBmdpi\u76EE\u5F55\u53BB\u627E\uFF0C \u5982\u679C\u627E\u5230\u4E86\uFF0C\u5C31\u62FF\u6765\u7528\u3002</li><li>\u5982\u679C\u6CA1\u627E\u5230\uFF0C\u5C31\u53BB\u9ED8\u8BA4\u7684drawble\u76EE\u5F55\u91CC\u53BB\u627E\uFF0C \u5982\u679C\u627E\u5230\u4E86\u5C31\u62FF\u6765\u7528\u3002</li><li>\u5982\u679C\u6CA1\u627E\u5230\uFF0C\u518D\u53BB\u6700\u4F4E\u7684ldpi\u76EE\u5F55\u91CC\u53BB\u627E\u3002\u5982\u679C\u627E\u5230\u4E86\uFF0C\u5C31\u62FF\u6765\u7528\u3002</li><li>\u5982\u679C\u6CA1\u627E\u5230\uFF0C \u90A3\u5C31\u662F\u6CA1\u627E\u5230\u4E86\uFF0C \u56FE\u7247\u65E0\u6CD5\u663E\u793A\u3002\uFF08\u4E0D\u8FC7\u4E00\u822C\u4E0D\u4F1A\u51FA\u73B0\u8FD9\u79CD\u73B0\u8C61\uFF0C\u56E0\u4E3A\u5982\u679C\u6BCF\u4E2A\u76EE\u5F55\u90FD\u6CA1\u6709\u8FD9\u4E2A\u56FE\u7247\u7684\u8BDD\uFF0C\u4F60\u662F\u7F16\u8BD1\u4E0D\u8FC7\u7684\uFF09</li></ul><p>\u8FD9\u91CC\u6709\u4E24\u70B9\u9700\u8981\u6CE8\u610F\uFF1A</p><ul><li><p>\u5148\u4F1A\u53BB\u6BD4\u81EA\u5DF1\u5BC6\u5EA6\u9AD8\u7684\u76EE\u5F55\u91CC\u53BB\u627E\uFF0C\u8FD9\u662F\u56E0\u4E3A\u56E0\u4E3A\u7CFB\u7EDF\u76F8\u4FE1\uFF0C\u4F60\u5728\u5BC6\u5EA6\u66F4\u9AD8\u7684\u76EE\u5F55\u91CC\u4F1A\u653E\u7F6E\u5206\u8FA8\u7387\u66F4\u5927\u7684\u56FE\u7247\uFF0C\u8FD9\u6837\u7684\u8BDD\u8FD9\u4E2A\u56FE\u7247\u4F1A\u88AB\u7F29\u5C0F\uFF0C\u4F46\u540C\u65F6\u663E\u793A\u6548\u679C\u4E0D\u4F1A\u6709\u635F\u5931\uFF0C\u4F46\u662F\u5982\u679C\u4F18\u5148\u53BB\u4F4E\u4E00\u7EA7\u522B\u7684\u76EE\u5F55\u53BB\u627E\u7684\u8BDD\uFF0C \u627E\u5230\u7684\u56FE\u7247\u5C31\u4F1A\u88AB\u653E\u5927\uFF0C\u8FD9\u6837\u7684\u8BDD\u8FD9\u4E2A\u56FE\u7247\u5C31\u4F1A\u88AB\u62C9\u626F\u6A21\u7CCA\u4E86\u3002 e.g. \u540C\u4E00\u5F20\u56FE\u7247\uFF0C\u4F60\u5728mdpi\u548Cxxhdpi\u76EE\u5F55\u5404\u653E\u4E86\u4E00\u4EFD\uFF0C \u8FD9\u4E2A\u5E94\u7528\u4F60\u73B0\u5728\u8FD0\u884C\u5728hdpi\u7684\u624B\u673A\u4E0A\uFF0C \u90A3\u5E94\u7528\u4F1A\u9009\u62E9\u54EA\u5F20\u56FE\u7247\u5462\u3002\u7B54\u6848\u662Fxxhdpi\u76EE\u5F55\u91CC\u7684\u3002\u5373\u4FBFhdpi\u79BBmdpi\u66F4\u8FD1\u4E00\u70B9\uFF01</p></li><li><p>\u5982\u679C\u5728mdpi\u91CC\u627E\u4E0D\u5230\u662F\u4E0D\u4F1A\u76F4\u63A5\u53BBldpi\u91CC\u627E\u7684\uFF0C \u800C\u662F\u5148\u53BB\u9ED8\u8BA4\u7684drawble\u76EE\u5F55\u91CC\u627E\uFF0C\u8FD9\u662Fdrawble\u76EE\u5F55\u548Cdrawble-mdpi\u662F\u4E00\u4E2A\u7EA7\u522B\u7684\u3002</p></li></ul><p><img src="https://raw.githubusercontent.com/CharonChui/Pictures/master/scale_drawable.png?raw=true" alt="image"><br> \u4E0A\u8868\u51E0\u70B9\u503C\u5F97\u6CE8\u610F\u7684\u5730\u65B9\uFF1A</p><ul><li><code>rawable</code>\u76EE\u5F55\u548C<code>drawable-mdpi</code>\u76EE\u5F55\u548C<code>dp</code>\u5230<code>px</code>\u7684\u8F6C\u6362\u5173\u7CFB\u662F\u4E00\u6837\u7684\u3002</li><li>\u5F53\u4F60\u653E\u4E00\u4E2A<code>120px*180px</code>\u7684\u56FE\u7247\u5230<code>drawable-hdpi</code>\u76EE\u5F55\uFF0C\u5982\u679C\u6B64\u5E94\u7528\u8FD0\u884C\u5728\u4E00\u4E2A<code>xhdpi</code>\u7684\u624B\u673A\u4E0A\uFF0C\u5219\u8FD9\u4E2A\u56FE\u7247\u4F1A\u88AB\u62C9\u626F\u5230<code>160px*240px</code>\u3002</li><li>\u6700\u540E\u4E00\u884C<code>dp-&gt;px</code>\uFF0C \u8BF4\u660E\u4E86\u5728\u4EE3\u7801\u6216\u8005\u5E03\u5C40\u6587\u4EF6\u4E2D\u58F0\u660E\u4E00\u4E2A<code>dp</code>\u503C\uFF0C \u8FD9\u4E2A\u503C\u5728\u4E0D\u540C\u5C4F\u5E55\u5BC6\u5EA6\u7684\u624B\u673A\u4E2D\u4F1A\u88AB\u4E58\u4EE5\u4E0D\u540C\u7684\u500D\u6570\u3002 \u6BD4\u5982\u4F60\u5728\u5E03\u5C40\u6587\u4EF6\u4E2D\u5199\u4E86\u4E00\u4E2A\u5BBD\u548C\u9AD8\u5206\u522B\u4E3A<code>120dp</code>\u548C<code>180dp</code>\u7684<code>LinearLayout</code>\uFF0C \u90A3\u4E48\u5F53\u8FD9\u4E2A\u5E94\u7528\u8FD0\u884C\u5728<code>xhdpi</code>\u7684\u624B\u673A\u4E0A\u65F6 \u5B83\u7684\u5B9E\u9645\u50CF\u7D20\u5C31\u4F1A\u88AB\u8F6C\u6362\u4E3A<code>240px*360px</code>\u3002 \u5982\u679C\u8FD0\u884C\u5728<code>ldpi</code>\u7684\u624B\u673A\u4E0A\uFF0C\u5C31\u53D8\u6210\u4E86<code>90px*135px</code>\u3002 \u4F46\u662F\u5728\u8FD9\u4E24\u4E2A\u624B\u673A\u4E2D\u663E\u793A\u7684\u533A\u57DF\u5927\u5C0F\u4ECE\u8089\u773C\u770B\uFF0C\u662F\u4E00\u6A21\u4E00\u6837\u5927\u7684\u3002</li></ul><p>UI\u7ED9\u5DE5\u7A0B\u5E08\u5207\u591A\u5927\u56FE\u662F\u5408\u9002\u7684\u3002<br> \u76F4\u63A5\u57FA\u4E8E<code>720*1280</code>\u7684\u89C6\u89C9\u7A3F\u5207\u4E00\u7248\u56FE\u7247\u5C31\u53EF\u4EE5\u4E86\u3002 \u5C06\u56FE\u7247\u53EA\u653E\u5230<code>xhdpi</code>\u76EE\u5F55\u4E2D\uFF0C \u8FD9\u6837\u7CFB\u7EDF\u4F1A\u5728\u4E0D\u540C\u5BC6\u5EA6\u5C4F\u5E55\u7684\u624B\u673A\u4E2D\u5BF9\u56FE\u7247\u8FDB\u884C\u5408\u7406\u7684\u7F29\u653E\uFF0C\u5982\u679C\u60F3\u5728<code>xxhdpi</code>\u7684\u624B\u673A\u4E0A\u663E\u793A\u7684\u5F88\u597D\uFF0C \u4E5F\u53EF\u4EE5\u57FA\u4E8E<code>1080P</code>\u7684\u5C4F\u5E55\u8BBE\u8BA1\u7136\u540E\u653E\u5230<code>xxhdpi</code>\u4E2D\uFF0C \u8FD9\u6837\u7684\u8BDD\u5C31\u517C\u5BB9\u6240\u6709\u4F4E\u5BC6\u5EA6\u5C4F\u5E55\u7684\u624B\u673A\uFF0C \u800C\u4E14\u4E5F\u4E0D\u4F1A\u51FA\u73B0\u56FE\u7247\u88AB\u62C9\u626F\u7684\u73B0\u8C61\u3002</p><p><em><strong>Note:</strong></em><code> The mipmap-xxxhdpi qualifier is necessary only to provide a launcher icon that can appear larger than usual on an xxhdpi device. You do not need to provide xxxhdpi assets for all your app&#39;s images.</code></p><hr><ul><li>\u90AE\u7BB1 \uFF1Acharon.chui@gmail.com</li><li>Good Luck!</li></ul>`,51),c=[t];function s(p,i){return d(),o("div",null,c)}var r=e(n,[["render",s],["__file","\u5C4F\u5E55\u9002\u914D.html.vue"]]);export{r as default};
