import{_ as e,o as c,c as d,e as o}from"./app.e2a3b496.js";const a={},r=o('<h1 id="\u6570\u636E\u52A0\u5BC6\u53CA\u89E3\u5BC6" tabindex="-1"><a class="header-anchor" href="#\u6570\u636E\u52A0\u5BC6\u53CA\u89E3\u5BC6" aria-hidden="true">#</a> \u6570\u636E\u52A0\u5BC6\u53CA\u89E3\u5BC6</h1><p>\u52A0\u5BC6<code>incode</code>\uFF1A\u5BF9\u660E\u6587(<code>plaintext</code>\u53EF\u8BFB\u61C2\u7684\u4FE1\u606F)\u8FDB\u884C\u7FFB\u8BD1\uFF0C\u4F7F\u7528\u4E0D\u540C\u7684\u7B97\u6CD5\u5BF9\u660E\u6587\u4EE5\u4EE3\u7801\u5F62\u5F0F(\u5BC6\u7801)\u5B9E\u65BD\u52A0\u5BC6\u8F6C\u6362\u6210\u5BC6\u6587(<code>ciphertext</code>)\u3002 \u8BE5\u8FC7\u7A0B\u7684\u9006\u8FC7\u7A0B\u79F0\u4E3A\u89E3\u5BC6(<code>descode</code>)\uFF0C\u5373\u5C06\u8BE5\u7F16\u7801\u4FE1\u606F\u8F6C\u5316\u4E3A\u660E\u6587\u7684\u8FC7\u7A0B\u3002</p><h2 id="\u5BF9\u79F0\u52A0\u5BC6-symmetric-cryptography" tabindex="-1"><a class="header-anchor" href="#\u5BF9\u79F0\u52A0\u5BC6-symmetric-cryptography" aria-hidden="true">#</a> \u5BF9\u79F0\u52A0\u5BC6(<code>Symmetric Cryptography</code>)</h2><p>\u5BF9\u79F0\u52A0\u5BC6\u662F\u6700\u5FEB\u901F\u3001\u6700\u7B80\u5355\u7684\u4E00\u79CD\u52A0\u5BC6\u65B9\u5F0F\uFF0C\u52A0\u5BC6(<code>encryption</code>\u4E0E\u89E3\u5BC6(<code>decryption</code>)\u7528\u7684\u662F\u540C\u6837\u7684\u5BC6\u94A5(<code>secret key</code>), \u8FD9\u79CD\u65B9\u6CD5\u5728\u5BC6\u7801\u5B66\u4E2D\u53EB\u505A\u5BF9\u79F0\u52A0\u5BC6\u7B97\u6CD5\u3002 \u5BF9\u79F0\u52A0\u5BC6\u6709\u5F88\u591A\u79CD\u7B97\u6CD5\uFF0C\u7531\u4E8E\u5B83\u6548\u7387\u5F88\u9AD8\uFF0C\u6240\u4EE5\u88AB\u5E7F\u6CDB\u4F7F\u7528\u5728\u5F88\u591A\u52A0\u5BC6\u534F\u8BAE\u7684\u6838\u5FC3\u5F53\u4E2D\u3002 \u5BF9\u79F0\u52A0\u5BC6\u901A\u5E38\u4F7F\u7528\u7684\u662F\u76F8\u5BF9\u8F83\u5C0F\u7684\u5BC6\u94A5\uFF0C\u4E00\u822C\u5C0F\u4E8E256<code>bit</code>\u3002\u56E0\u4E3A\u5BC6\u94A5\u8D8A\u5927\uFF0C\u52A0\u5BC6\u8D8A\u5F3A\uFF0C\u4F46\u52A0\u5BC6\u4E0E\u89E3\u5BC6\u7684\u8FC7\u7A0B\u8D8A\u6162\u3002 \u5982\u679C\u4F60\u53EA\u75281<code>bit</code>\u6765\u505A\u8FD9\u4E2A\u5BC6\u94A5\uFF0C\u90A3\u9ED1\u5BA2\u4EEC\u53EF\u4EE5\u5148\u8BD5\u7740\u75280\u6765\u89E3\u5BC6\uFF0C\u4E0D\u884C\u7684\u8BDD\u5C31\u518D\u75281\u89E3\uFF1B \u4F46\u5982\u679C\u4F60\u7684\u5BC6\u94A5\u67091<code>MB</code>\u5927\uFF0C\u9ED1\u5BA2\u4EEC\u53EF\u80FD\u6C38\u8FDC\u4E5F\u65E0\u6CD5\u7834\u89E3\uFF0C\u4F46\u52A0\u5BC6\u548C\u89E3\u5BC6\u7684\u8FC7\u7A0B\u8981\u82B1\u8D39\u5F88\u957F\u7684\u65F6\u95F4\u3002 \u5BC6\u94A5\u7684\u5927\u5C0F\u65E2\u8981\u7167\u987E\u5230\u5B89\u5168\u6027\uFF0C\u4E5F\u8981\u7167\u987E\u5230\u6548\u7387\uFF0C\u662F\u4E00\u4E2A<code>trade-off</code>\u3002</p><p>\u5BF9\u79F0\u52A0\u5BC6\u7B97\u6CD5\u4ECB\u7ECD:</p><ul><li>DES</li></ul><p><code>DES</code>\u5168\u79F0\u4E3A<code>Data Encryption Standard</code>,\u5373\u6570\u636E\u52A0\u5BC6\u6807\u51C6\uFF0C\u662F\u4E00\u79CD\u4F7F\u7528\u5BC6\u94A5\u52A0\u5BC6\u7684\u5757\u7B97\u6CD5\uFF0C 1976\u5E74\u88AB\u7F8E\u56FD\u8054\u90A6\u653F\u5E9C\u7684\u56FD\u5BB6\u6807\u51C6\u5C40\u786E\u5B9A\u4E3A\u8054\u90A6\u8D44\u6599\u5904\u7406\u6807\u51C6(<code>FIPS</code>)\uFF0C\u968F\u540E\u5728\u56FD\u9645\u4E0A\u5E7F\u6CDB\u6D41\u4F20\u5F00\u6765\u3002</p><p><code>DES</code>\u7B97\u6CD5\u7684\u5165\u53E3\u53C2\u6570\u6709\u4E09\u4E2A:<code>Key</code>\u3001<code>Data</code>\u3001<code>Mode</code>\u3002\u5176\u4E2D<code>Key</code>\u4E3A8\u4E2A\u5B57\u8282\u517164\u4F4D,\u662F<code>DES</code>\u7B97\u6CD5\u7684\u5DE5\u4F5C\u5BC6\u94A5;<code>Data</code>\u4E5F\u4E3A8\u4E2A\u5B57\u828264\u4F4D, \u662F\u8981\u88AB\u52A0\u5BC6\u6216\u88AB\u89E3\u5BC6\u7684\u6570\u636E;<code>Mode</code>\u4E3A<code>DES</code>\u7684\u5DE5\u4F5C\u65B9\u5F0F,\u6709\u4E24\u79CD:\u52A0\u5BC6\u6216\u89E3\u5BC6\u3002 <code>DES</code>\u7B97\u6CD5\u628A64\u4F4D\u7684\u660E\u6587\u8F93\u5165\u5757\u53D8\u4E3A64\u4F4D\u7684\u5BC6\u6587\u8F93\u51FA\u5757,\u5B83\u6240\u4F7F\u7528\u7684\u5BC6\u94A5\u4E5F\u662F64\u4F4D\u3002</p><ul><li>AES <code>AES</code>\u5168\u7A0B\u4E3A<code>Advanced Encryption Standard</code>,\u5373\u9AD8\u7EA7\u52A0\u5BC6\u6807\u51C6\uFF0C\u5728\u5BC6\u7801\u5B66\u4E2D\u53C8\u79F0<code>Rijndael</code>\u52A0\u5BC6\u6CD5\uFF0C \u662F\u7F8E\u56FD\u8054\u90A6\u653F\u5E9C\u91C7\u7528\u7684\u4E00\u79CD\u533A\u5757\u52A0\u5BC6\u6807\u51C6\u3002\u8FD9\u4E2A\u6807\u51C6\u7528\u6765\u66FF\u4EE3\u539F\u5148\u7684<code>DES</code>\uFF0C\u5DF2\u7ECF\u88AB\u591A\u65B9\u5206\u6790\u4E14\u5E7F\u4E3A\u5168\u4E16\u754C\u6240\u4F7F\u7528\u3002 <code>AES</code>\u52A0\u5BC6\u6570\u636E\u5757\u5206\u7EC4\u957F\u5EA6\u5FC5\u987B\u4E3A128\u6BD4\u7279\uFF0C\u5BC6\u94A5\u957F\u5EA6\u53EF\u4EE5\u662F128\u6BD4\u7279\u3001192\u6BD4\u7279\u3001256\u6BD4\u7279\u4E2D\u7684\u4EFB\u610F\u4E00\u4E2A\uFF08\u5982\u679C\u6570\u636E\u5757\u53CA\u5BC6\u94A5\u957F\u5EA6\u4E0D\u8DB3\u65F6\uFF0C\u4F1A\u8865\u9F50\uFF09</li></ul><h2 id="\u975E\u5BF9\u79F0\u52A0\u5BC6-asymmetric-cryptography" tabindex="-1"><a class="header-anchor" href="#\u975E\u5BF9\u79F0\u52A0\u5BC6-asymmetric-cryptography" aria-hidden="true">#</a> \u975E\u5BF9\u79F0\u52A0\u5BC6(<code>Asymmetric Cryptography</code>)</h2><p>1976\u5E74\uFF0C\u7F8E\u56FD\u5B66\u8005<code>Dime</code>\u548C<code>Henman</code>\u4E3A\u89E3\u51B3\u4FE1\u606F\u516C\u5F00\u4F20\u9001\u548C\u5BC6\u94A5\u7BA1\u7406\u95EE\u9898\uFF0C\u63D0\u51FA\u4E00\u79CD\u65B0\u7684\u5BC6\u94A5\u4EA4\u6362\u534F\u8BAE\uFF0C\u5141\u8BB8\u5728\u4E0D\u5B89\u5168\u7684\u5A92\u4F53\u4E0A\u7684\u901A\u8BAF\u53CC\u65B9\u4EA4\u6362\u4FE1\u606F\uFF0C \u5B89\u5168\u5730\u8FBE\u6210\u4E00\u81F4\u7684\u5BC6\u94A5\uFF0C\u8FD9\u5C31\u662F\u201C\u516C\u5F00\u5BC6\u94A5\u7CFB\u7EDF\u201D\u3002\u76F8\u5BF9\u4E8E\u201C\u5BF9\u79F0\u52A0\u5BC6\u7B97\u6CD5\u201D\u8FD9\u79CD\u65B9\u6CD5\u4E5F\u53EB\u505A\u201C\u975E\u5BF9\u79F0\u52A0\u5BC6\u7B97\u6CD5\u201D\u3002 \u975E\u5BF9\u79F0\u52A0\u5BC6\u4E3A\u6570\u636E\u7684\u52A0\u5BC6\u4E0E\u89E3\u5BC6\u63D0\u4F9B\u4E86\u4E00\u4E2A\u975E\u5E38\u5B89\u5168\u7684\u65B9\u6CD5\uFF0C\u5B83\u4F7F\u7528\u4E86\u4E00\u5BF9\u5BC6\u94A5\uFF0C\u516C\u94A5(<code>public key</code>)\u548C\u79C1\u94A5(<code>private key</code>)\u3002 \u79C1\u94A5\u53EA\u80FD\u7531\u4E00\u65B9\u5B89\u5168\u4FDD\u7BA1\uFF0C\u4E0D\u80FD\u5916\u6CC4\uFF0C\u800C\u516C\u94A5\u5219\u53EF\u4EE5\u53D1\u7ED9\u4EFB\u4F55\u8BF7\u6C42\u5B83\u7684\u4EBA\u3002\u975E\u5BF9\u79F0\u52A0\u5BC6\u4F7F\u7528\u8FD9\u5BF9\u5BC6\u94A5\u4E2D\u7684\u4E00\u4E2A\u8FDB\u884C\u52A0\u5BC6\uFF0C\u800C\u89E3\u5BC6\u5219\u9700\u8981\u53E6\u4E00\u4E2A\u5BC6\u94A5\u3002 \u6BD4\u5982\uFF0C\u4F60\u5411\u94F6\u884C\u8BF7\u6C42\u516C\u94A5\uFF0C\u94F6\u884C\u5C06\u516C\u94A5\u53D1\u7ED9\u4F60\uFF0C\u4F60\u4F7F\u7528\u516C\u94A5\u5BF9\u6D88\u606F\u52A0\u5BC6\uFF0C\u90A3\u4E48\u53EA\u6709\u79C1\u94A5\u7684\u6301\u6709\u4EBA--\u94F6\u884C\u624D\u80FD\u5BF9\u4F60\u7684\u6D88\u606F\u89E3\u5BC6\u3002 \u4E0E\u5BF9\u79F0\u52A0\u5BC6\u4E0D\u540C\u7684\u662F\uFF0C\u94F6\u884C\u4E0D\u9700\u8981\u5C06\u79C1\u94A5\u901A\u8FC7\u7F51\u7EDC\u53D1\u9001\u51FA\u53BB\uFF0C\u56E0\u6B64\u5B89\u5168\u6027\u5927\u5927\u63D0\u9AD8\u3002 \u76EE\u524D\u6700\u5E38\u7528\u7684\u975E\u5BF9\u79F0\u52A0\u5BC6\u7B97\u6CD5\u662F<code>RSA</code>\u7B97\u6CD5\uFF0C\u662F<code>Rivest</code>, <code>Shamir</code>\u548C<code>Adleman</code>\u4E8E1978\u5E74\u53D1\u660E\u3002</p><p>\u867D\u7136\u975E\u5BF9\u79F0\u52A0\u5BC6\u5F88\u5B89\u5168\uFF0C\u4F46\u662F\u548C\u5BF9\u79F0\u52A0\u5BC6\u6BD4\u8D77\u6765\uFF0C\u5B83\u975E\u5E38\u7684\u6162\uFF0C\u6240\u4EE5\u6211\u4EEC\u8FD8\u662F\u8981\u7528\u5BF9\u79F0\u52A0\u5BC6\u6765\u4F20\u9001\u6D88\u606F\uFF0C \u4F46\u5BF9\u79F0\u52A0\u5BC6\u6240\u4F7F\u7528\u7684\u5BC6\u94A5\u6211\u4EEC\u53EF\u4EE5\u901A\u8FC7\u975E\u5BF9\u79F0\u52A0\u5BC6\u7684\u65B9\u5F0F\u53D1\u9001\u51FA\u53BB\u3002</p><h2 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h2><ul><li>\u5BF9\u79F0\u52A0\u5BC6\u52A0\u5BC6\u4E0E\u89E3\u5BC6\u4F7F\u7528\u7684\u662F\u540C\u6837\u7684\u5BC6\u94A5\uFF0C\u6240\u4EE5\u901F\u5EA6\u5FEB\uFF0C\u4F46\u7531\u4E8E\u9700\u8981\u5C06\u5BC6\u94A5\u5728\u7F51\u7EDC\u4F20\u8F93\uFF0C\u6240\u4EE5\u5B89\u5168\u6027\u4E0D\u9AD8\u3002</li><li>\u975E\u5BF9\u79F0\u52A0\u5BC6\u4F7F\u7528\u4E86\u4E00\u5BF9\u5BC6\u94A5\uFF0C\u516C\u94A5\u4E0E\u79C1\u94A5\uFF0C\u6240\u4EE5\u5B89\u5168\u6027\u9AD8\uFF0C\u4F46\u52A0\u5BC6\u4E0E\u89E3\u5BC6\u901F\u5EA6\u6162\u3002</li><li>\u89E3\u51B3\u7684\u529E\u6CD5\u662F\u5C06\u5BF9\u79F0\u52A0\u5BC6\u7684\u5BC6\u94A5\u4F7F\u7528\u975E\u5BF9\u79F0\u52A0\u5BC6\u7684\u516C\u94A5\u8FDB\u884C\u52A0\u5BC6\uFF0C\u7136\u540E\u53D1\u9001\u51FA\u53BB\uFF0C\u63A5\u6536\u65B9\u4F7F\u7528\u79C1\u94A5\u8FDB\u884C\u89E3\u5BC6\u5F97\u5230\u5BF9\u79F0\u52A0\u5BC6\u7684\u5BC6\u94A5\uFF0C \u7136\u540E\u53CC\u65B9\u53EF\u4EE5\u4F7F\u7528\u5BF9\u79F0\u52A0\u5BC6\u6765\u8FDB\u884C\u6C9F\u901A\u3002</li></ul><hr><ul><li>\u90AE\u7BB1 \uFF1Acharon.chui@gmail.com</li><li>Good Luck!</li></ul>',16),i=[r];function t(n,h){return c(),d("div",null,i)}var p=e(a,[["render",t],["__file","\u6570\u636E\u52A0\u5BC6\u53CA\u89E3\u5BC6.html.vue"]]);export{p as default};
