import{_ as i,r as l,o as a,c as r,a as e,b as d,e as n,d as c}from"./app.e2a3b496.js";const t={},s=n('<h1 id="\u97F3\u89C6\u9891\u57FA\u7840\u77E5\u8BC6" tabindex="-1"><a class="header-anchor" href="#\u97F3\u89C6\u9891\u57FA\u7840\u77E5\u8BC6" aria-hidden="true">#</a> \u97F3\u89C6\u9891\u57FA\u7840\u77E5\u8BC6</h1><h2 id="\u57FA\u672C\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#\u57FA\u672C\u6982\u5FF5" aria-hidden="true">#</a> \u57FA\u672C\u6982\u5FF5</h2><ul><li><em><strong>\u5A92\u4F53</strong></em>\uFF1A\u662F\u8868\u793A\uFF0C\u4F20\u8F93\uFF0C\u5B58\u50A8\u4FE1\u606F\u7684\u8F7D\u4F53\uFF0C\u5E38\u4EBA\u4EEC\u89C1\u5230\u7684\u6587\u5B57\u3001\u58F0\u97F3\u3001\u56FE\u50CF\u3001\u56FE\u5F62\u7B49\u90FD\u662F\u8868\u793A\u4FE1\u606F\u7684\u5A92\u4F53\u3002</li><li><em><strong>\u591A\u5A92\u4F53</strong></em>\uFF1A\u662F\u58F0\u97F3\u3001\u52A8\u753B\u3001\u6587\u5B57\u3001\u56FE\u50CF\u548C\u5F55\u50CF\u7B49\u5404\u79CD\u5A92\u4F53\u7684\u7EC4\u5408\uFF0C\u4EE5\u56FE\u6587\u5E76\u8302\uFF0C\u751F\u52A8\u6D3B\u6CFC\u7684\u52A8\u6001\u5F62\u5F0F\u8868\u73B0\u51FA\u6765\uFF0C\u7ED9\u4EBA\u4EE5\u5F88\u5F3A\u7684\u89C6\u89C9\u51B2\u51FB\u529B\uFF0C\u7559\u4E0B\u6DF1\u523B\u5370\u8C61.</li><li><em><strong>\u591A\u5A92\u4F53\u6280\u672F</strong></em>\uFF1A\u662F\u5C06\u6587\u5B57\u3001\u58F0\u97F3\u3001\u56FE\u5F62\u3001\u9759\u6001\u56FE\u50CF\u3001\u52A8\u6001\u56FE\u50CF\u4E0E\u8BA1\u7B97\u96C6\u6210\u5728\u4E00\u8D77\u7684\u6280\u672F\u3002\u5B83\u8981\u89E3\u51B3\u7684\u95EE\u9898\u662F\u8BA1\u7B97\u673A\u8FDB\u4E00\u6B65\u5E2E\u52A9\u4EBA\u7C7B\u6309\u6700\u81EA\u7136\u7684\u548C\u6700\u4E60\u60EF\u7684\u65B9\u5F0F\u63A5\u53D7\u548C\u5904\u7406\u4FE1\u606F\u3002</li><li><em><strong>\u6D41\u5A92\u4F53</strong></em>\uFF1A\u6D41\u5A92\u4F53\u662F\u6307\u91C7\u7528\u6D41\u5F0F\u4F20\u8F93\u7684\u65B9\u5F0F\u5728<code>Internet</code>\u64AD\u653E\u7684\u8FDE\u7EED\u65F6\u57FA\u5A92\u4F53\u683C\u5F0F\uFF0C\u5B9E\u9645\u6307\u7684\u662F\u4E00\u79CD\u65B0\u7684\u5A92\u4F53\u4F20\u9001\u65B9\u5F0F\uFF0C\u800C\u4E0D\u662F\u4E00\u79CD\u65B0\u7684\u5A92\u4F53\u683C\u5F0F\uFF08\u5728\u7F51\u7EDC\u4E0A\u4F20\u8F93\u97F3/\u89C6\u9891\u7B49\u591A\u5A92\u4F53\u4FE1\u606F\u73B0\u5728\u4E3B\u8981\u6709\u4E0B\u8F7D\u548C\u6D41\u5F0F\u4F20\u8F93\u4E24\u79CD\u65B9\u5F0F\uFF09\u6D41\u5F0F\u4F20\u8F93\u5206\u4E24\u79CD\u65B9\u6CD5\uFF1A\u5B9E\u65F6\u6D41\u5F0F\u4F20\u8F93\u65B9\u5F0F(<code>Realtime Streaming</code>)\u548C\u987A\u5E8F\u6D41\u5F0F\u4F20\u8F93\u65B9\u5F0F(<code>Progressive Streaming</code>)\u3002</li><li><em><strong>\u591A\u5A92\u4F53\u6587\u4EF6</strong></em>:\u662F\u65E2\u5305\u62EC\u89C6\u9891\u53C8\u5305\u62EC\u97F3\u9891\uFF0C\u751A\u81F3\u8FD8\u5E26\u6709\u811A\u672C\u7684\u4E00\u4E2A\u96C6\u5408\uFF0C\u4E5F\u53EF\u4EE5\u53EB\u5BB9\u5668\u3002</li><li><em><strong>\u5A92\u4F53\u7F16\u7801</strong></em>:\u662F\u6587\u4EF6\u5F53\u4E2D\u7684\u89C6\u9891\u548C\u97F3\u9891\u6240\u91C7\u7528\u7684\u538B\u7F29\u7B97\u6CD5\u3002\u4E5F\u5C31\u662F\u8BF4\u4E00\u4E2A<code>avi</code>\u7684\u6587\u4EF6\uFF0C\u5F53\u4E2D\u7684\u89C6\u9891\u7F16\u7801\u6709\u53EF\u80FD\u662F<code>A</code>\uFF0C\u4E5F\u53EF\u80FD\u662F<code>B</code>\uFF0C\u800C\u5176\u97F3\u9891\u7F16\u7801\u6709\u53EF\u80FD\u662F<code>1</code>\uFF0C\u4E5F\u6709\u53EF\u80FD\u662F<code>2</code>\u3002</li><li><em><strong>\u8F6C\u7801</strong></em>:\u6307\u5C06\u4E00\u6BB5\u591A\u5A92\u4F53\u5305\u62EC\u97F3\u9891\u3001\u89C6\u9891\u6216\u8005\u5176\u4ED6\u7684\u5185\u5BB9\u4ECE\u4E00\u79CD\u7F16\u7801\u683C\u5F0F\u8F6C\u6362\u6210\u4E3A\u53E6\u5916\u4E00\u79CD\u7F16\u7801\u683C\u5F0F\u3002</li><li><em><strong>\u5E27</strong></em>:\u5E27\u5C31\u662F\u4E00\u6BB5\u6570\u636E\u7684\u7EC4\u5408\uFF0C\u5B83\u662F\u6570\u636E\u4F20\u8F93\u7684\u57FA\u672C\u5355\u4F4D\u3002\u5C31\u662F\u5F71\u50CF\u52A8\u753B\u4E2D\u6700\u5C0F\u5355\u4F4D\u7684\u5355\u5E45\u5F71\u50CF\u753B\u9762\uFF0C\u76F8\u5F53\u4E8E\u7535\u5F71\u80F6\u7247\u4E0A\u7684\u6BCF\u4E00\u683C\u955C\u5934\u3002\u4E00\u5E27\u5C31\u662F\u4E00\u526F\u9759\u6B62\u7684\u753B\u9762\uFF0C\u8FDE\u7EED\u7684\u5E27\u5C31\u5F62\u6210\u52A8\u753B\uFF0C\u5982\u7535\u89C6\u56FE\u50CF\u7B49\u3002</li><li><em><strong>\u89C6\u9891</strong></em>\uFF1A\u8FDE\u7EED\u7684\u56FE\u8C61\u53D8\u5316\u6BCF\u79D2\u8D85\u8FC724\u5E27(<code>Frame</code>)\u753B\u9762\u4EE5\u4E0A\u65F6\uFF0C\u6839\u636E\u89C6\u89C9\u6682\u7559\u539F\u7406\uFF0C\u4EBA\u773C\u65E0\u6CD5\u8FA8\u522B\u5355\u5E45\u7684\u9759\u6001\u753B\u9762\uFF0C\u770B\u4E0A\u53BB\u662F\u5E73\u6ED1\u8FDE\u7EED\u7684\u89C6\u89C9\u6548\u679C\uFF0C\u8FD9\u6837\u8FDE\u7EED\u7684\u753B\u9762\u53EB\u505A\u89C6\u9891.</li><li><em><strong>\u97F3\u9891</strong></em>:\u4EBA\u7C7B\u80FD\u542C\u5230\u7684\u58F0\u97F3\u90FD\u6210\u4E3A\u97F3\u9891\uFF0C\u4F46\u662F\u4E00\u822C\u6211\u4EEC\u6240\u8BF4\u5230\u7684\u97F3\u9891\u65F6\u5B58\u50A8\u5728\u8BA1\u7B97\u673A\u91CC\u7684\u58F0\u97F3\u3002</li><li><em><strong>\u7801\u7387</strong></em>:\u7801\u7387\u5C31\u662F\u6570\u636E\u4F20\u8F93\u65F6\u5355\u4F4D\u65F6\u95F4\u4F20\u9001\u7684\u6570\u636E\u4F4D\u6570,\u4E00\u822C\u6211\u4EEC\u7528\u7684\u5355\u4F4D\u662F<code>kbps</code>\u5373\u5343\u4F4D\u6BCF\u79D2\u3002 \u901A\u4FD7\u4E00\u70B9\u7684\u7406\u89E3\u5C31\u662F\u53D6\u6837\u7387\uFF0C\u5355\u4F4D\u65F6\u95F4\u5185\u53D6\u6837\u7387\u8D8A\u5927\uFF0C\u7CBE\u5EA6\u5C31\u8D8A\u9AD8\uFF0C\u5904\u7406\u51FA\u6765\u7684\u6587\u4EF6\u5C31\u8D8A\u63A5\u8FD1\u539F\u59CB\u6587\u4EF6\uFF0C\u4F46\u662F\u6587\u4EF6\u4F53\u79EF\u4E0E\u53D6\u6837\u7387\u662F\u6210\u6B63\u6BD4\u7684\uFF0C\u6240\u4EE5\u51E0\u4E4E\u6240\u6709\u7684\u7F16\u7801\u683C\u5F0F\u91CD\u89C6\u7684\u90FD\u662F\u5982\u4F55\u7528\u6700\u4F4E\u7684\u7801\u7387\u8FBE\u5230\u6700\u5C11\u7684\u5931\u771F\u3002\u4F46\u662F\u56E0\u4E3A\u7F16\u7801\u7B97\u6CD5\u4E0D\u4E00\u6837\uFF0C\u6240\u4EE5\u4E5F\u4E0D\u80FD\u7528\u7801\u7387\u6765\u7EDF\u4E00\u8861\u91CF\u97F3\u8D28\u6216\u8005\u753B\u8D28.</li><li><em><strong>\u5E27\u7387</strong></em>:\u5E27/\u79D2(<code>frames per second</code>)\u7684\u7F29\u5199\u5E27\u7387\u5373\u6BCF\u79D2\u663E\u793A\u5E27\u6570\uFF0C\u5E27\u7387\u8868\u793A\u56FE\u5F62\u5904\u7406\u5668\u5904\u7406\u573A\u65F6\u6BCF\u79D2\u949F\u80FD\u591F\u66F4\u65B0\u7684\u6B21\u6570\u3002\u9AD8\u7684\u5E27\u7387\u53EF\u4EE5\u5F97\u5230\u66F4\u6D41\u7545\u3001\u66F4\u903C\u771F\u7684\u52A8\u753B\u3002\u4E00\u822C\u6765\u8BF4<code>30fps</code>\u5C31\u662F\u53EF\u4EE5\u63A5\u53D7\u7684\uFF0C\u4F46\u662F\u5C06\u6027\u80FD\u63D0\u5347\u81F3<code>60fps</code>\u5219\u53EF\u4EE5\u660E\u663E\u63D0\u5347\u4EA4\u4E92\u611F\u548C\u903C\u771F\u611F\uFF0C\u4F46\u662F\u4E00\u822C\u6765\u8BF4\u8D85\u8FC7<code>75fps</code>\u4E00\u822C\u5C31\u4E0D\u5BB9\u6613\u5BDF\u89C9\u5230\u6709\u660E\u663E\u7684\u6D41\u7545\u5EA6\u63D0\u5347\u4E86\u3002\u5982\u679C\u5E27\u7387\u8D85\u8FC7\u5C4F\u5E55\u5237\u65B0\u7387\u53EA\u4F1A\u6D6A\u8D39\u56FE\u5F62\u5904\u7406\u7684\u80FD\u529B\uFF0C\u56E0\u4E3A\u76D1\u89C6\u5668\u4E0D\u80FD\u4EE5\u8FD9\u4E48\u5FEB\u7684\u901F\u5EA6\u66F4\u65B0\uFF0C\u8FD9\u6837\u8D85\u8FC7\u65B0\u7387\u7684\u5E27\u7387\u5C31\u6D6A\u8D39\u6389\u4E86\u3002</li><li><em><strong>\u5173\u952E\u5E27</strong></em>:\u76F8\u5F53\u4E8E\u4E8C\u7EF4\u52A8\u753B\u4E2D\u7684\u539F\u753B\uFF0C\u6307\u89D2\u8272\u6216\u8005\u7269\u4F53\u8FD0\u52A8\u6216\u53D8\u5316\u4E2D\u7684\u5173\u952E\u52A8\u4F5C\u6240\u5904\u7684\u90A3\u4E00\u5E27\uFF0C\u5B83\u5305\u542B\u4E86\u56FE\u50CF\u7684\u6240\u6709\u4FE1\u606F\uFF0C\u540E\u6765\u5E27\u4EC5\u5305\u542B\u4E86\u6539\u53D8\u4E86\u7684\u4FE1\u606F\u3002\u5982\u679C\u4F60\u6CA1\u6709\u8DB3\u591F\u7684\u5173\u952E\u5E27\uFF0C\u4F60\u7684\u5F71\u7247\u54C1\u8D28\u53EF\u80FD\u6BD4\u8F83\u5DEE\uFF0C\u56E0\u4E3A\u6240\u6709\u7684\u5E27\u4ECE\u522B\u7684\u5E27\u5904\u4EA7\u751F\u3002\u5BF9\u4E8E\u4E00\u822C\u7684\u7528\u9014\uFF0C\u4E00\u4E2A\u6BD4\u8F83\u597D\u7684\u539F\u5219\u662F\u6BCF5\u79D2\u8BBE\u4E00\u4E2A\u5173\u952E\u952E\u3002\u4F46\u5982\u679C\u65F6\u90A3\u79CD\u5B9E\u65F6\u4F20\u8F93\u7684\u6D41\u6587\u4EF6\uFF0C\u90A3\u4E48\u8981\u8003\u8651\u4F20\u8F93\u7F51\u7EDC\u7684\u53EF\u9760\u5EA6\uFF0C\u6240\u4EE5\u89811\u52302\u79D2\u589E\u52A0\u4E00\u4E2A\u5173\u952E\u5E27\u3002</li></ul><h2 id="\u89C6\u9891\u683C\u5F0F-\u5C01\u88C5\u683C\u5F0F-\u5BB9\u5668" tabindex="-1"><a class="header-anchor" href="#\u89C6\u9891\u683C\u5F0F-\u5C01\u88C5\u683C\u5F0F-\u5BB9\u5668" aria-hidden="true">#</a> \u89C6\u9891\u683C\u5F0F(\u5C01\u88C5\u683C\u5F0F/\u5BB9\u5668)</h2><p>\u76EE\u524D\u6211\u4EEC\u7ECF\u5E38\u89C1\u7684\u89C6\u9891\u683C\u5F0F\u65E0\u975E\u5C31\u662F\u4E24\u5927\u7C7B\uFF1A</p><ol><li>\u5F71\u50CF\u683C\u5F0F\uFF08Video\uFF09</li><li>\u6D41\u5A92\u4F53\u683C\u5F0F\uFF08Stream Video\uFF09</li></ol><p>\u5728\u5F71\u50CF\u683C\u5F0F\u4E2D\u8FD8\u53EF\u4EE5\u6839\u636E\u51FA\u5904\u5212\u5206\u4E3A\u4E09\u5927\u79CD:</p><ol><li><p><code>AVI</code>\u683C\u5F0F<br><code>AVI</code>\u82F1\u6587\u5168\u79F0\u4E3A<code>Audio Video Interleaved</code>\uFF0C\u5373\u97F3\u9891\u89C6\u9891\u4EA4\u9519\u683C\u5F0F\uFF0C\u662F\u5FAE\u8F6F\u516C\u53F8\u4E8E1992\u5E7411\u6708\u63A8\u51FA\u3001\u4F5C\u4E3A\u5176<code>Windows</code>\u89C6\u9891\u8F6F\u4EF6\u4E00\u90E8\u5206\u7684\u4E00\u79CD\u591A\u5A92\u4F53\u5BB9\u5668\u683C\u5F0F\u3002 <code>AVI</code>\u6587\u4EF6\u5C06\u97F3\u9891\uFF08\u8BED\u97F3\uFF09\u548C\u89C6\u9891\uFF08\u5F71\u50CF\uFF09\u6570\u636E\u5305\u542B\u5728\u4E00\u4E2A\u6587\u4EF6\u5BB9\u5668\u4E2D\uFF0C\u5141\u8BB8\u97F3\u89C6\u9891\u540C\u6B65\u56DE\u653E\u3002\u7C7B\u4F3C<code>DVD</code>\u89C6\u9891\u683C\u5F0F\uFF0C<code>AVI</code>\u6587\u4EF6\u652F\u6301\u591A\u4E2A\u97F3\u89C6\u9891\u6D41\u3002<code>AVI</code>\u4FE1\u606F\u4E3B\u8981\u5E94\u7528\u5728\u591A\u5A92\u4F53\u5149\u76D8\u4E0A\uFF0C\u7528\u6765\u4FDD\u5B58\u7535\u89C6\u3001\u7535\u5F71\u7B49\u5404\u79CD\u5F71\u50CF\u4FE1\u606F\u3002<br> \u5176\u4E2D\u6570\u636E\u5757\u5305\u542B\u5B9E\u9645\u6570\u636E\u6D41\uFF0C\u5373\u56FE\u50CF\u548C\u58F0\u97F3\u5E8F\u5217\u6570\u636E\u3002\u8FD9\u662F\u6587\u4EF6\u7684\u4E3B\u4F53\uFF0C\u4E5F\u662F\u51B3\u5B9A\u6587\u4EF6\u5BB9\u91CF\u7684\u4E3B\u8981\u90E8\u5206\u3002\u89C6\u9891\u6587\u4EF6\u7684\u5927\u5C0F\u7B49\u4E8E\u8BE5\u6587\u4EF6\u7684\u6570\u636E\u7387\u4E58\u4EE5\u8BE5\u89C6\u9891\u64AD\u653E\u7684\u65F6\u95F4\u957F\u5EA6\uFF0C \u7D22\u5F15\u5757\u5305\u62EC\u6570\u636E\u5757\u5217\u8868\u548C\u5B83\u4EEC\u5728\u6587\u4EF6\u4E2D\u7684\u4F4D\u7F6E\uFF0C\u4EE5\u63D0\u4F9B\u6587\u4EF6\u5185\u6570\u636E\u968F\u673A\u5B58\u53D6\u80FD\u529B\u3002\u6587\u4EF6\u5934\u5305\u62EC\u6587\u4EF6\u7684\u901A\u7528\u4FE1\u606F\uFF0C\u5B9A\u4E49\u6570\u636E\u683C\u5F0F\uFF0C\u6240\u7528\u7684\u538B\u7F29\u7B97\u6CD5\u7B49\u53C2\u6570\u3002<br><code>AVI</code>\u6CA1\u6709<code>MPEG</code>\u8FD9\u4E48\u590D\u6742\uFF0C\u4ECE<code>Windows 3.1</code>\u65F6\u4EE3\uFF0C\u5B83\u5C31\u5DF2\u7ECF\u9762\u4E16\u4E86\u3002\u5B83\u6700\u76F4\u63A5\u7684\u4F18\u70B9\u5C31\u662F\u517C\u5BB9\u597D\u3001\u8C03\u7528\u65B9\u4FBF\u800C\u4E14\u56FE\u8C61\u8D28\u91CF\u597D\uFF0C\u56E0\u6B64\u4E5F\u5E38\u5E38\u4E0E<code>DVD</code>\u76F8\u5E76\u79F0\u3002 \u4F46\u5B83\u7684\u7F3A\u70B9\u4E5F\u662F\u5341\u5206\u660E\u663E\u7684\uFF1A\u4F53\u79EF\u5927\u3002\u4E5F\u662F\u56E0\u4E3A\u8FD9\u4E00\u70B9\uFF0C\u6211\u4EEC\u624D\u770B\u5230\u4E86<code>MPEG-1</code>\u548C<code>MPEG-4</code>\u7684\u8BDE\u751F\u30022\u5C0F\u65F6\u5F71\u50CF\u7684<code>AVI</code>\u6587\u4EF6\u7684\u4F53\u79EF\u4E0E<code>MPEG-2</code>\u76F8\u5DEE\u65E0\u51E0\uFF0C \u4E0D\u8FC7\u8FD9\u53EA\u662F\u9488\u5BF9\u6807\u51C6\u5206\u8FA8\u7387\u800C\u8A00\u7684\uFF1A\u6839\u636E\u4E0D\u540C\u7684\u5E94\u7528\u8981\u6C42\uFF0C<code>AVI</code>\u7684\u5206\u8FA8\u7387\u53EF\u4EE5\u968F\u610F\u8C03\u3002\u7A97\u53E3\u8D8A\u5927\uFF0C\u6587\u4EF6\u7684\u6570\u636E\u91CF\u4E5F\u5C31\u8D8A\u5927\u3002\u964D\u4F4E\u5206\u8FA8\u7387\u53EF\u4EE5\u5927\u5E45\u51CF\u4F4E\u5B83\u7684\u4F53\u79EF\uFF0C \u4F46\u56FE\u8C61\u8D28\u91CF\u5C31\u5FC5\u7136\u53D7\u635F\u3002\u4E0E<code>MPEG-2</code>\u683C\u5F0F\u6587\u4EF6\u4F53\u79EF\u5DEE\u4E0D\u591A\u7684\u60C5\u51B5\u4E0B\uFF0C<code>AVI</code>\u683C\u5F0F\u7684\u89C6\u9891\u8D28\u91CF\u76F8\u5BF9\u800C\u8A00\u8981\u5DEE\u4E0D\u5C11\uFF0C\u4F46\u5236\u4F5C\u8D77\u6765\u5BF9\u7535\u8111\u7684\u914D\u7F6E\u8981\u6C42\u4E0D\u9AD8\uFF0C\u7ECF\u5E38\u6709\u4EBA\u5148\u5F55\u5236\u597D\u4E86<code>AVI</code>\u683C\u5F0F\u7684\u89C6\u9891\uFF0C\u518D\u8F6C\u6362\u4E3A\u5176\u4ED6\u683C\u5F0F\u3002</p></li><li><p><code>MOV</code>\u683C\u5F0F <code>MOV</code>\u5373<code>QuickTime</code>\u5F71\u7247\u683C\u5F0F\uFF0C\u5B83\u662F<code>Apple</code>\u516C\u53F8\u5F00\u53D1\u7684\u4E00\u79CD\u97F3\u9891\u3001\u89C6\u9891\u6587\u4EF6\u683C\u5F0F\uFF0C\u7528\u4E8E\u5B58\u50A8\u5E38\u7528\u6570\u5B57\u5A92\u4F53\u7C7B\u578B\u3002\u5F53\u9009\u62E9<code>QuickTime\uFF08*.mov\uFF09</code>\u4F5C\u4E3A\u201C\u4FDD\u5B58\u7C7B\u578B\u201D\u65F6\uFF0C\u52A8\u753B\u5C06\u4FDD\u5B58\u4E3A<code>\xB7mov</code>\u6587\u4EF6\u3002<code>QuickTime</code>\u7528\u4E8E\u4FDD\u5B58\u97F3\u9891\u548C\u89C6\u9891\u4FE1\u606F\uFF0C\u5305\u62EC<code>Apple Mac OS\uFF0CMicrosoftWindows95/98/NT/2003/XP/VISTA</code>\uFF0C\u751A\u81F3<code>WINDOWS7</code>\u5728\u5185\u7684\u6240\u6709\u4E3B\u6D41\u7535\u8111\u5E73\u53F0\u652F\u6301\u3002<code>QuickTime</code>\u56E0\u5177\u6709\u8DE8\u5E73\u53F0\u3001\u5B58\u50A8\u7A7A\u95F4\u8981\u6C42\u5C0F\u7B49\u6280\u672F\u7279\u70B9\uFF0C\u800C\u91C7\u7528\u4E86\u6709\u635F\u538B\u7F29\u65B9\u5F0F\u7684<code>MOV</code>\u683C\u5F0F\u6587\u4EF6\uFF0C\u753B\u9762\u6548\u679C\u8F83AVI\u683C\u5F0F\u8981\u7A0D\u5FAE\u597D\u4E00\u4E9B\u3002\u5230\u76EE\u524D\u4E3A\u6B62\uFF0C\u5B83\u5171\u67094\u4E2A\u7248\u672C\uFF0C\u5176\u4E2D\u4EE5<code>4.0</code>\u7248\u672C\u7684\u538B\u7F29\u7387\u6700\u597D\u3002\u8FD9\u79CD\u7F16\u7801\u652F\u630116\u4F4D\u56FE\u50CF\u6DF1\u5EA6\u7684\u5E27\u5185\u538B\u7F29\u548C\u5E27\u95F4\u538B\u7F29\uFF0C\u5E27\u7387\u6BCF\u79D210\u5E27\u4EE5\u4E0A\u3002\u8FD9\u79CD\u683C\u5F0F\u6709\u4E9B\u975E\u7F16\u8F6F\u4EF6\u4E5F\u53EF\u4EE5\u5BF9\u5B83\u5B9E\u884C\u5904\u7406\uFF0C\u5176\u4E2D\u5305\u62EC<code>ADOBE</code>\u516C\u53F8\u7684\u4E13\u4E1A\u7EA7\u591A\u5A92\u4F53\u89C6\u9891\u5904\u7406\u8F6F\u4EF6<code>AFTEREFFECTS\u548CPREMIERE</code>\u3002</p></li><li><p><code>MPEG/MPG/DAT</code>: \u8FD9\u662F\u7531\u56FD\u9645\u6807\u51C6\u5316\u7EC4\u7EC7<code>ISO(International Standards Organization)</code>\u4E0E<code>IEC(International Electronic Committee)</code>\u8054\u5408\u5F00\u53D1\u7684\u4E00\u79CD\u7F16\u7801\u89C6\u9891\u683C\u5F0F\u3002<code>MPEG</code>\u662F\u8FD0\u52A8\u56FE\u50CF\u538B\u7F29\u7B97\u6CD5\u7684\u56FD\u9645\u6807\u51C6\uFF0C\u73B0\u5DF2\u88AB\u51E0\u4E4E\u6240\u6709\u7684\u8BA1\u7B97\u673A\u5E73\u53F0\u5171\u540C\u652F\u6301\u3002MPEG\u4E5F\u662F<code>Motion Picture Experts Group</code>\u7684\u7F29\u5199\u3002\u8FD9\u7C7B\u683C\u5F0F\u5305\u62EC\u4E86<code>MPEG-1, MPEG-2</code>\u548C<code>MPEG-4</code>\u5728\u5185\u7684\u591A\u79CD\u89C6\u9891\u683C\u5F0F\u3002<code>MPEG-1</code>\u76F8\u4FE1\u662F\u5927\u5BB6\u63A5\u89E6\u5F97\u6700\u591A\u7684\u4E86\uFF0C\u56E0\u4E3A\u76EE\u524D\u5176\u6B63\u5728\u88AB\u5E7F\u6CDB\u5730\u5E94\u7528\u5728<code>VCD</code>\u7684\u5236\u4F5C\u548C\u4E00\u4E9B\u89C6\u9891\u7247\u6BB5\u4E0B\u8F7D\u7684\u7F51\u7EDC\u5E94\u7528\u4E0A\u9762\uFF0C\u5927\u90E8\u5206\u7684<code>VCD</code>\u90FD\u662F\u7528 <code>MPEG1</code>\u683C\u5F0F\u538B\u7F29\u7684( \u523B\u5F55\u8F6F\u4EF6\u81EA\u52A8\u5C06<code>MPEG1</code>\u8F6C\u4E3A<code>.DAT</code>\u683C\u5F0F)\uFF0C\u4F7F\u7528<code>MPEG-1</code>\u7684\u538B\u7F29\u7B97\u6CD5\uFF0C\u53EF\u4EE5\u628A\u4E00\u90E8120\u5206\u949F\u957F\u7684\u7535\u5F71\u538B\u7F29\u52301.2GB\u5DE6\u53F3\u5927\u5C0F\u3002<code>MPEG-2</code>\u5219\u662F\u5E94\u7528\u5728<code>DVD</code> \u7684\u5236\u4F5C\uFF0C\u540C\u65F6\u5728\u4E00\u4E9B<code>HDTV</code>\uFF08\u9AD8\u6E05\u6670\u7535\u89C6\u5E7F\u64AD\uFF09\u548C\u4E00\u4E9B\u9AD8\u8981\u6C42\u89C6\u9891\u7F16\u8F91\u3001\u5904\u7406\u4E0A\u9762\u4E5F\u6709\u76F8\u5F53\u591A\u7684\u5E94\u7528\u3002\u4F7F\u7528<code>MPEG-2</code>\u7684\u538B\u7F29\u7B97\u6CD5\u538B\u7F29\u4E00\u90E8120\u5206\u949F\u957F\u7684\u7535\u5F71\u53EF\u4EE5\u538B\u7F29\u52305-8GB\u7684\u5927\u5C0F\uFF08<code>MPEG2</code>\u7684\u56FE\u50CF\u8D28\u91CF<code>MPEG-1</code>\u4E0E\u5176\u65E0\u6CD5\u6BD4\u62DF\u7684\uFF09\u3002</p></li></ol><p>\u5728\u6D41\u5A92\u4F53\u683C\u5F0F\u4E2D\u540C\u6837\u8FD8\u53EF\u4EE5\u5212\u5206\u4E3A\u4E09\u79CD:</p><ol><li><p><code>RM</code>\u683C\u5F0F <code>Real Networks</code>\u516C\u53F8\u6240\u5236\u5B9A\u7684\u97F3\u9891/\u89C6\u9891\u538B\u7F29\u89C4\u8303<code>Real Media</code>\u4E2D\u7684\u4E00\u79CD\uFF0C<code>Real Player</code>\u80FD\u505A\u7684\u5C31\u662F\u5229\u7528<code>Internet</code>\u8D44\u6E90\u5BF9\u8FD9\u4E9B\u7B26\u5408<code>Real Media</code>\u6280\u672F\u89C4\u8303\u7684\u97F3\u9891/\u89C6\u9891\u8FDB\u884C\u5B9E\u51B5\u8F6C\u64AD\u3002\u5728<code>Real Media</code>\u89C4\u8303\u4E2D\u4E3B\u8981\u5305\u62EC\u4E09\u7C7B\u6587\u4EF6\uFF1A<code>RealAudio</code>\u3001<code>Real Video</code>\u548C<code>Real Flash</code>\uFF08<code>Real Networks</code>\u516C\u53F8\u4E0E<code>Macromedia</code>\u516C\u53F8\u5408\u4F5C\u63A8\u51FA\u7684\u65B0\u4E00\u4EE3\u9AD8\u538B\u7F29\u6BD4\u52A8\u753B\u683C\u5F0F\uFF09\u3002<code>REAL VIDEO</code>\uFF08RA\u3001RAM\uFF09\u683C\u5F0F\u7531\u4E00\u5F00\u59CB\u5C31\u662F\u5B9A\u4F4D\u5C31\u662F\u5728\u89C6\u9891\u6D41\u5E94\u7528\u65B9\u9762\u7684\uFF0C\u4E5F\u53EF\u4EE5\u8BF4\u662F\u89C6\u9891\u6D41\u6280\u672F\u7684\u59CB\u521B\u8005\u3002\u5B83\u53EF\u4EE5\u5728\u752856K<code>MODEM</code>\u62E8\u53F7\u4E0A\u7F51\u7684\u6761\u4EF6\u5B9E\u73B0\u4E0D\u95F4\u65AD\u7684\u89C6\u9891\u64AD\u653E\uFF0C\u4ECE<code>RealVideo</code>\u7684\u5B9A\u4F4D\u6765\u770B\uFF0C\u5C31\u662F\u727A\u7272\u753B\u9762\u8D28\u91CF\u6765\u6362\u53D6\u53EF\u8FDE\u7EED\u89C2\u770B\u6027\u3002\u5176\u5B9E<code>RealVideo</code>\u4E5F\u53EF\u4EE5\u5B9E\u73B0\u4E0D\u9519\u7684\u753B\u9762\u8D28\u91CF\uFF0C\u7531\u4E8E<code>RealVideo</code>\u53EF\u4EE5\u62E5\u6709\u975E\u5E38\u9AD8\u7684\u538B\u7F29\u6548\u7387\uFF0C\u5F88\u591A\u4EBA\u628A<code>VCD</code>\u7F16\u7801\u6210<code>RealVideo</code>\u683C\u5F0F\u7684\uFF0C\u8FD9\u6837\u4E00\u6765\uFF0C\u4E00\u5F20\u5149\u76D8\u4E0A\u53EF\u4EE5\u5B58\u653E\u597D\u51E0\u90E8\u7535\u5F71\u3002<code>REAL VIDEO</code>\u5B58\u5728\u989C\u8272\u8FD8\u539F\u4E0D\u51C6\u786E\u7684\u95EE\u9898\uFF0C<code>RealVideo</code>\u5C31\u4E0D\u592A\u9002\u5408\u4E13\u4E1A\u7684\u573A\u5408\uFF0C\u4F46<code>RealVideo</code>\u51FA\u8272\u7684\u538B\u7F29\u6548\u7387\u548C\u652F\u6301\u6D41\u5F0F\u64AD\u653E\u7684\u7279\u5F81\uFF0C\u4F7F\u5F97<code>RealVideo</code>\u5728\u7F51\u7EDC\u548C\u5A31\u4E50\u573A\u5408\u5360\u6709\u4E0D\u9519\u7684\u5E02\u573A\u4EFD\u989D\u3002</p></li><li><p><code>MOV/QT</code>\u683C\u5F0F <code>MOV</code>\u4E5F\u53EF\u4EE5\u4F5C\u4E3A\u4E00\u79CD\u6D41\u6587\u4EF6\u683C\u5F0F\u3002<code>QuickTime</code>\u80FD\u591F\u901A\u8FC7<code>Internet</code>\u63D0\u4F9B\u5B9E\u65F6\u7684\u6570\u5B57\u5316\u4FE1\u606F\u6D41\u3001\u5DE5\u4F5C\u6D41\u4E0E\u6587\u4EF6\u56DE\u653E\u529F\u80FD\uFF0C\u4E3A\u4E86\u9002\u5E94\u8FD9\u4E00\u7F51\u7EDC\u591A\u5A92\u4F53\u5E94\u7528\uFF0C<code>QuickTime</code>\u4E3A\u591A\u79CD\u6D41\u884C\u7684\u6D4F\u89C8\u5668\u8F6F\u4EF6\u63D0\u4F9B\u4E86\u76F8\u5E94\u7684<code>QuickTime Viewer</code>\u63D2\u4EF6\uFF0C\u80FD\u591F\u5728\u6D4F\u89C8\u5668\u4E2D\u5B9E\u73B0\u591A\u5A92\u4F53\u6570\u636E\u7684\u5B9E\u65F6\u56DE\u653E\u3002</p></li><li><p><code>ASF</code>\u683C\u5F0F <code>ASF</code>(<code>Advanced Streaming format</code>\u9AD8\u7EA7\u6D41\u683C\u5F0F)\u3002<code>ASF</code>\u662F<code>MICROSOFT</code>\u4E3A\u4E86\u548C\u73B0\u5728\u7684<code>Real player</code>\u7ADE\u4E89\u800C\u53D1\u5C55\u51FA\u6765\u7684\u4E00\u79CD\u53EF\u4EE5\u76F4\u63A5\u5728\u7F51\u4E0A\u89C2\u770B\u89C6\u9891\u8282\u76EE\u7684\u6587\u4EF6\u538B\u7F29\u683C\u5F0F\u3002<code>ASF</code>\u4F7F\u7528\u4E86<code>MPEG4</code>\u7684\u538B\u7F29\u7B97\u6CD5\uFF0C\u538B\u7F29\u7387\u548C\u56FE\u50CF\u7684\u8D28\u91CF\u90FD\u5F88\u4E0D\u9519\u3002\u56E0\u4E3A<code>ASF</code>\u662F\u4EE5\u4E00\u4E2A\u53EF\u4EE5\u5728\u7F51\u4E0A\u5373\u65F6\u89C2\u8D4F\u7684\u89C6\u9891\u201C\u6D41\u201D\u683C\u5F0F\u5B58\u5728\u7684\uFF0C\u6240\u4EE5\u5B83\u7684\u56FE\u50CF\u8D28\u91CF\u6BD4<code>VCD</code>\u5DEE\u4E00\u70B9\u70B9\u5E76\u4E0D\u51FA\u5947\uFF0C\u4F46\u6BD4\u540C\u662F\u89C6\u9891\u201C\u6D41\u201D\u683C\u5F0F\u7684<code>RAM</code>\u683C\u5F0F\u8981\u597D\u3002 <code>ASF</code>\u652F\u6301\u4EFB\u610F\u7684\u538B\u7F29/\u89E3\u538B\u7F29\u7F16\u7801\u65B9\u5F0F\uFF0C\u5E76\u53EF\u4EE5\u4F7F\u7528\u4EFB\u4F55\u4E00\u79CD\u5E95\u5C42\u7F51\u7EDC\u4F20\u8F93\u534F\u8BAE\uFF0C\u5177\u6709\u5F88\u5927\u7684\u7075\u6D3B\u6027\u3002<code>ASF</code>\u6D41\u6587\u4EF6\u7684\u6570\u636E\u901F\u7387\u53EF\u4EE5\u5728<code>28.8Kbps</code>\u5230<code>3Mbps</code>\u4E4B\u95F4\u53D8\u5316\u3002\u7528\u6237\u53EF\u4EE5\u6839\u636E\u81EA\u5DF1\u5E94\u7528\u73AF\u5883\u548C\u7F51\u7EDC\u6761\u4EF6\u9009\u62E9\u4E00\u4E2A\u5408\u9002\u7684\u901F\u7387\uFF0C\u5B9E\u73B0<code>VOD</code>\u70B9\u64AD\u548C\u76F4\u64AD\u3002</p></li><li><p><code>FLV</code>\u683C\u5F0F<code>FLV</code>\u662F<code>FLASH VIDEO</code>\u7684\u7B80\u79F0\uFF0C\u4E00\u79CD\u6D41\u5A92\u4F53\u5C01\u88C5\u683C\u5F0F\uFF0C<code>FLV</code>\u6D41\u5A92\u4F53\u683C\u5F0F\u662F\u968F\u7740<code>Flash MX</code>\u7684\u63A8\u51FA\u53D1\u5C55\u800C\u6765\u7684\u89C6\u9891\u683C\u5F0F\u3002\u7531\u4E8E\u5B83\u5F62\u6210\u7684\u6587\u4EF6\u6781\u5C0F\u3001\u52A0\u8F7D\u901F\u5EA6\u6781\u5FEB\uFF0C\u4F7F\u5F97\u7F51\u7EDC\u89C2\u770B\u89C6\u9891\u6587\u4EF6\u6210\u4E3A\u53EF\u80FD\uFF0C\u5B83\u7684\u51FA\u73B0\u6709\u6548\u5730\u89E3\u51B3\u4E86\u89C6\u9891\u6587\u4EF6\u5BFC\u5165<code>Flash</code>\u540E\uFF0C\u4F7F\u5BFC\u51FA\u7684<code>SWF</code>\u6587\u4EF6\u4F53\u79EF\u5E9E\u5927\uFF0C\u4E0D\u80FD\u5728\u7F51\u7EDC\u4E0A\u5F88\u597D\u7684\u4F7F\u7528\u7B49\u95EE\u9898\u3002\u56E0\u6B64<code>FLV</code>\u683C\u5F0F\u6210\u4E3A\u4E86\u5F53\u4ECA\u4E3B\u6D41\u89C6\u9891\u683C\u5F0F</p></li></ol><h2 id="\u97F3\u89C6\u9891\u538B\u7F29\u7F16\u7801\u6807\u51C6" tabindex="-1"><a class="header-anchor" href="#\u97F3\u89C6\u9891\u538B\u7F29\u7F16\u7801\u6807\u51C6" aria-hidden="true">#</a> \u97F3\u89C6\u9891\u538B\u7F29\u7F16\u7801\u6807\u51C6</h2><p>\u591A\u5A92\u4F53\u7F16\u8F91\u7801\u65B9\u5F0F\u5C31\u662F\u6307\u901A\u8FC7\u7279\u5B9A\u7684\u538B\u7F29\u6280\u672F\uFF0C\u5C06\u67D0\u4E2A\u89C6\u9891\u683C\u5F0F\u7684\u6587\u4EF6\u8F6C\u6362\u6210\u53E6\u4E00\u79CD\u89C6\u9891\u683C\u5F0F\u6587\u4EF6\u7684\u65B9\u5F0F\uFF0C\u73B0\u5728\u4E3B\u8981\u7684\u7F16\u7801\u65B9\u5F0F\u6709:</p><ul><li><p><code>MPEG</code>\u7CFB\u5217:\u7531\u56FD\u9645\u6807\u51C6\u7EC4\u7EC7\u673A\u6784(<code>ISO</code>)\u4E0B\u5C5E\u7684\u8FD0\u52A8\u56FE\u8C61\u4E13\u5BB6\u7EC4(<code>MPEG</code>)\u5F00\u53D1\u3002\u89C6\u9891\u7F16\u7801\u65B9\u9762\u4E3B\u8981\u662F<code>Mpeg1</code>\u3001<code>Mpeg2</code>\u3001<code>Mpeg4</code>\u3001<code>Mpeg4 AVC</code>\u3002\u97F3\u9891\u7F16\u7801\u65B9\u9762\u4E3B\u8981\u662F<code>MPEG Audio Layer 1/2</code>\u3001<code>MPEG Audio Layer 3</code>\u3001<code>MPEG-2 AAC</code>\u3001<code>MPEG-4 AAC</code>\u7B49\u7B49:</p><ul><li><code>MPEG-1</code>\u7B2C\u4E8C\u90E8\u5206\uFF0C\u4E3B\u8981\u4F7F\u7528\u5728<code>VCD</code>\u4E0A\uFF0C\u6709\u4E9B\u5728\u7EBF\u89C6\u9891\u4E5F\u4F7F\u7528\u8FD9\u79CD\u683C\u5F0F\u3002\u8BE5\u7F16\u89E3\u7801\u5668\u7684\u8D28\u91CF\u5927\u81F4\u4E0A\u548C\u539F\u6709\u7684<code>VHS</code>\u5F55\u50CF\u5E26\u76F8\u5F53\u3002</li><li><code>MPEG-2</code>\u7B2C\u4E8C\u90E8\u5206\uFF0C\u7B49\u540C\u4E8E<code>H.262</code>\uFF0C\u4F7F\u7528\u5728<code>DVD</code>\u3001<code>SVCD</code>\u548C\u5927\u591A\u6570\u6570\u5B57\u89C6\u9891\u5E7F\u64AD\u7CFB\u7EDF\u548C\u6709\u7EBF\u5206\u5E03\u7CFB\u7EDF\u4E2D\u3002</li><li><code>MPEG-4</code>\u7B2C\u4E8C\u90E8\u5206\uFF0C\u53EF\u4EE5\u4F7F\u7528\u5728\u7F51\u7EDC\u4F20\u8F93\u3001\u5E7F\u64AD\u548C\u5A92\u4F53\u5B58\u50A8\u4E0A\u3002\u6BD4\u8D77<code>MPEG-2</code>\u7B2C\u4E8C\u90E8\u5206\u548C\u7B2C\u4E00\u7248\u7684<code>H.263</code>\uFF0C\u5B83\u7684\u538B\u7F29\u6027\u80FD\u6709\u6240\u63D0\u9AD8\u3002</li><li><code>MPEG-4</code>\u7B2C\u5341\u90E8\u5206\uFF0C\u7B49\u540C\u4E8E<code>H.264</code>\uFF0C\u662F\u8FD9\u4E24\u4E2A\u7F16\u7801\u7EC4\u7EC7\u5408\u4F5C\u8BDE\u751F\u7684\u6807\u51C6\u3002</li></ul></li><li><p><code>H.26X</code>\u7CFB\u5217\uFF1A\u7531\u56FD\u9645\u7535\u4F20\u89C6\u8BAF\u8054\u76DF\u8FDC\u7A0B\u901A\u4FE1\u6807\u51C6\u5316\u7EC4\u7EC7(<code>ITU-T</code>)\u4E3B\u5BFC\uFF0C\u5305\u62EC<code>H.261</code>\u3001<code>H.262</code>\u3001<code>H.263</code>\u3001<code>H.264</code>\u3001<code>H.265</code>\u3002</p><ul><li><code>H.261</code>\u4E3B\u8981\u7528\u4E8E\u8001\u7684\u89C6\u9891\u4F1A\u8BAE\u548C\u89C6\u9891\u7535\u8BDD\u7CFB\u7EDF\u3002\u662F\u7B2C\u4E00\u4E2A\u4F7F\u7528\u7684\u6570\u5B57\u89C6\u9891\u538B\u7F29\u6807\u51C6\u3002\u5B9E\u8D28\u4E0A\u8BF4\uFF0C\u4E4B\u540E\u7684\u6240\u6709\u7684\u6807\u51C6\u89C6\u9891\u7F16\u89E3\u7801\u5668\u90FD\u662F\u57FA\u4E8E\u5B83\u8BBE\u8BA1\u7684\u3002</li><li><code>H.262</code>\u7B49\u540C\u4E8E<code>MPEG-2</code>\u7B2C\u4E8C\u90E8\u5206\uFF0C\u4F7F\u7528\u5728<code>DVD</code>\u3001<code>SVCD</code>\u548C\u5927\u591A\u6570\u6570\u5B57\u89C6\u9891\u5E7F\u64AD\u7CFB\u7EDF\u548C\u6709\u7EBF\u5206\u5E03\u7CFB\u7EDF\u4E2D\u3002</li><li><code>H.263</code>\u4E3B\u8981\u7528\u4E8E\u89C6\u9891\u4F1A\u8BAE\u3001\u89C6\u9891\u7535\u8BDD\u548C\u7F51\u7EDC\u89C6\u9891\u76F8\u5173\u4EA7\u54C1\u3002\u5728\u5BF9\u9010\u884C\u626B\u63CF\u7684\u89C6\u9891\u6E90\u8FDB\u884C\u538B\u7F29\u7684\u65B9\u9762\uFF0C<code>H.263</code> \u6BD4\u5B83\u4E4B\u524D\u7684\u89C6\u9891\u7F16\u7801\u6807\u51C6\u5728\u6027\u80FD\u4E0A\u6709\u4E86\u8F83\u5927\u7684\u63D0\u5347\u3002\u5C24\u5176\u662F\u5728\u4F4E\u7801\u7387\u7AEF\uFF0C\u5B83\u53EF\u4EE5\u5728\u4FDD\u8BC1\u4E00\u5B9A\u8D28\u91CF\u7684\u524D\u63D0\u4E0B\u5927\u5927\u7684\u8282\u7EA6\u7801\u7387\u3002</li><li><code>H.264</code>\u7B49\u540C\u4E8E<code>MPEG-4</code>\u7B2C\u5341\u90E8\u5206\uFF0C\u4E5F\u88AB\u79F0\u4E3A\u9AD8\u7EA7\u89C6\u9891\u7F16\u7801(<code>Advanced Video Coding</code>\uFF0C\u7B80\u79F0 <code>AVC</code>)\uFF0C\u662F\u4E00\u79CD\u89C6\u9891\u538B\u7F29\u6807\u51C6\uFF0C\u4E00\u79CD\u88AB\u5E7F\u6CDB\u4F7F\u7528\u7684\u9AD8\u7CBE\u5EA6\u89C6\u9891\u7684\u5F55\u5236\u3001\u538B\u7F29\u548C\u53D1\u5E03\u683C\u5F0F\u3002\u8BE5\u6807\u51C6\u5F15\u5165\u4E86\u4E00\u7CFB\u5217\u65B0\u7684\u80FD\u591F\u5927\u5927\u63D0\u9AD8\u538B\u7F29\u6027\u80FD\u7684\u6280\u672F\uFF0C\u5E76\u80FD\u591F\u540C\u65F6\u5728\u9AD8\u7801\u7387\u7AEF\u548C\u4F4E\u7801\u7387\u7AEF\u5927\u5927\u8D85\u8D8A\u4EE5\u524D\u7684\u8BF8\u6807\u51C6\u3002</li><li><code>H.265</code>\u88AB\u79F0\u4E3A\u9AD8\u6548\u7387\u89C6\u9891\u7F16\u7801(<code>High Efficiency Video Coding</code>\uFF0C\u7B80\u79F0<code>HEVC</code>)\u662F\u4E00\u79CD\u89C6\u9891\u538B\u7F29\u6807\u51C6\uFF0C\u662F<code>H.264</code>\u7684\u7EE7\u4EFB\u8005\u3002<code>HEVC</code> \u88AB\u8BA4\u4E3A\u4E0D\u4EC5\u63D0\u5347\u56FE\u50CF\u8D28\u91CF\uFF0C\u540C\u65F6\u4E5F\u80FD\u8FBE\u5230<code>H.264</code>\u4E24\u500D\u7684\u538B\u7F29\u7387\uFF08\u7B49\u540C\u4E8E\u540C\u6837\u753B\u9762\u8D28\u91CF\u4E0B\u6BD4\u7279\u7387\u51CF\u5C11\u4E8650%\uFF09\uFF0C\u53EF\u652F\u6301<code>4K</code> \u5206\u8FA8\u7387\u751A\u81F3\u5230\u8D85\u9AD8\u753B\u8D28\u7535\u89C6\uFF0C\u6700\u9AD8\u5206\u8FA8\u7387\u53EF\u8FBE\u5230<code>8192\xD74320</code>(<code>8K</code>\u5206\u8FA8\u7387)\uFF0C\u8FD9\u662F\u76EE\u524D\u53D1\u5C55\u7684\u8D8B\u52BF\u3002</li></ul></li><li><p>\u5FAE\u8F6F<code>Windows Media</code>\u7CFB\u5217:\u89C6\u9891\u7F16\u7801\u6709<code>Mpeg-4 v1/v2/v3</code>\u3001<code>Windows Media Video 7/8/9/10</code>\uFF1B\u97F3\u9891\u7F16\u7801\u6709<code>Windows Media audeo v1/v2/7/8/9</code>\u3002</p></li><li><p><code>Real Media</code>\u7CFB\u5217:\u89C6\u9891\u7F16\u7801\u6709<code>RealVideo G2</code>\u3001<code>RealVideo 8/9/10</code>\uFF1B\u97F3\u9891\u7F16\u7801\u6709<code>RealAudio cook/sipro</code>\u3001<code>RealAudio AAC/AACPlus</code>\u7B49\u3002</p></li><li><p><code>QuickTime</code>\u7CFB\u5217:\u89C6\u9891\u7F16\u7801\u6709<code>Sorenson Video 3</code>\u3001<code>Apple MPEG-4</code>\u3001<code>Apple H.264</code>\uFF1B\u97F3\u9891\u7F16\u7801\u6709<code>QDesign Music 2</code>\u3001<code>Apple MPEG-4 AAC</code>\u3002</p></li><li><p>\u5176\u5B83\u5982\uFF1A<code>Ogg</code>\u3001<code>On2-vpx</code>\u3001<code>flash vidio</code>\uFF0C\u4EE5\u53CA<code>M-JPEG</code>\u89C6\u9891\u538B\u7F29\u65B9\u5F0F\u3002</p></li></ul><h4 id="\u97F3\u9891\u7F16\u89E3\u7801\u65B9\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u97F3\u9891\u7F16\u89E3\u7801\u65B9\u5F0F" aria-hidden="true">#</a> \u97F3\u9891\u7F16\u89E3\u7801\u65B9\u5F0F</h4><p>\u89C6\u9891\u4E2D\u9664\u4E86\u753B\u9762\u901A\u5E38\u8FD8\u6709\u58F0\u97F3\uFF0C\u6240\u4EE5\u8FD9\u5C31\u6D89\u53CA\u5230\u97F3\u9891\u7F16\u89E3\u7801\u3002\u5728\u89C6\u9891\u4E2D\u7ECF\u5E38\u4F7F\u7528\u7684\u97F3\u9891\u7F16\u7801\u65B9\u5F0F\u6709:</p><ul><li><code>AAC</code>\u{1F626}<code>Advanced Audio Coding</code>)\uFF0C\u662F\u7531<code>Fraunhofer IIS</code>\u3001<code>\u675C\u6BD4\u5B9E\u9A8C\u5BA4</code>\u3001<code>AT&amp;T</code>\u3001<code>Sony</code>\u7B49\u516C\u53F8\u5171\u540C\u5F00\u53D1\uFF0C\u57281997\u5E74\u63A8\u51FA\u7684\u57FA\u4E8E<code>MPEG-2</code> \u7684\u97F3\u9891\u7F16\u7801\u6280\u672F\u30022000\u5E74\uFF0C<code>MPEG-4</code>\u6807\u51C6\u51FA\u73B0\u540E\uFF0C<code>AAC</code>\u91CD\u65B0\u96C6\u6210\u4E86\u5176\u7279\u6027\uFF0C\u52A0\u5165\u4E86<code>SBR</code>\u6280\u672F\u548C<code>PS</code>\u6280\u672F\uFF0C\u4E3A\u4E86\u533A\u522B\u4E8E\u4F20\u7EDF\u7684<code>MPEG-2 AAC</code>\u53C8\u79F0\u4E3A<code>MPEG-4 AAC</code>\u3002</li><li><code>MP3</code>\u{1F626}<code>MPEG-1 or MPEG-2 Audio Layer III</code>)\uFF0C\u662F\u5F53\u66FE\u7ECF\u975E\u5E38\u6D41\u884C\u7684\u4E00\u79CD\u6570\u5B57\u97F3\u9891\u7F16\u7801\u548C\u6709\u635F\u538B\u7F29\u683C\u5F0F\uFF0C\u5B83\u88AB\u8BBE\u8BA1\u6765\u5927\u5E45\u964D\u4F4E\u97F3\u9891\u6570\u636E\u91CF\u3002\u5B83\u662F\u57281991 \u5E74\uFF0C\u7531\u4F4D\u4E8E\u5FB7\u56FD\u57C3\u5C14\u6717\u6839\u7684\u7814\u7A76\u7EC4\u7EC7<code>Fraunhofer-Gesellschaft</code>\u7684\u4E00\u7EC4\u5DE5\u7A0B\u5E08\u53D1\u660E\u548C\u6807\u51C6\u5316\u7684\u3002<code>MP3</code>\u7684\u666E\u53CA\u66FE\u5BF9\u97F3\u4E50\u4EA7\u4E1A\u9020\u6210\u6781\u5927\u7684\u51B2\u51FB\u4E0E\u5F71\u54CD\u3002</li><li><code>WMA</code>\u{1F626}<code>Windows Media Audio</code>)\u7531\u5FAE\u8F6F\u516C\u53F8\u5F00\u53D1\u7684\u4E00\u79CD\u6570\u5B57\u97F3\u9891\u538B\u7F29\u683C\u5F0F\uFF0C\u672C\u8EAB\u5305\u62EC\u6709\u635F\u548C\u65E0\u635F\u538B\u7F29\u683C\u5F0F\u3002</li></ul><h2 id="\u4E00\u4E9B\u97F3\u89C6\u9891\u7684\u53C2\u6570\u542B\u4E49" tabindex="-1"><a class="header-anchor" href="#\u4E00\u4E9B\u97F3\u89C6\u9891\u7684\u53C2\u6570\u542B\u4E49" aria-hidden="true">#</a> \u4E00\u4E9B\u97F3\u89C6\u9891\u7684\u53C2\u6570\u542B\u4E49</h2><p><em><strong>\u58F0\u9053</strong></em>\uFF1A\u76EE\u524D\u4EBA\u4EEC\u6240\u4F7F\u7528\u7684\u5404\u79CD\u58F0\u573A\u6280\u672F\u89C4\u8303\u975E\u5E38\u591A\uFF0C\u4F46\u6700\u5E38\u89C1\u7684\u51E0\u4E4E\u90FD\u6765\u81EA\u4E09\u5BB6\u516C\u53F8\uFF0C\u4ED6\u4EEC\u662F<code>Dolby</code>\uFF08\u675C\u6BD4\uFF09\u3001<code>HTX</code>\u548C<code>DTS</code>\u3002</p><p>\u58F0\u5361\u6240\u652F\u6301\u7684\u58F0\u9053\u6570\u662F\u8861\u91CF\u58F0\u5361\u6863\u6B21\u7684\u91CD\u8981\u6307\u6807\u4E4B\u4E00\uFF0C\u4ECE\u5355\u58F0\u9053\u5230\u6700\u65B0\u7684\u73AF\u7ED5\u7ACB\u4F53\u58F0\uFF0C\u4E0B\u9762\u4E00\u4E00\u8BE6\u7EC6\u4ECB\u7ECD\uFF1A</p><ul><li>\u5355\u58F0\u9053\uFF1A\u5355\u58F0\u9053\u662F\u6BD4\u8F83\u539F\u59CB\u7684\u58F0\u97F3\u590D\u5236\u5F62\u5F0F\uFF0C\u65E9\u671F\u7684\u58F0\u5361\u91C7\u7528\u7684\u6BD4\u8F83\u666E\u904D\u3002\u5F53\u901A\u8FC7\u4E24\u4E2A\u626C\u58F0\u5668\u56DE\u653E\u5355\u58F0\u9053\u4FE1\u606F\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u53EF\u4EE5\u660E\u663E\u611F\u89C9\u5230\u58F0\u97F3\u662F\u4ECE\u4E24\u4E2A\u97F3\u7BB1\u4E2D\u95F4\u4F20\u9012\u5230\u6211\u4EEC\u8033\u6735\u91CC\u7684\u3002 \u8FD9\u79CD\u7F3A\u4E4F\u4F4D\u7F6E\u611F\u7684\u5F55\u5236\u65B9\u5F0F\u7528\u73B0\u5728\u7684\u773C\u5149\u770B\u81EA\u7136\u662F\u5F88\u843D\u540E\u7684\uFF0C\u4F46\u5728\u58F0\u5361\u521A\u521A\u8D77\u6B65\u65F6\uFF0C\u5DF2\u7ECF\u662F\u975E\u5E38\u5148\u8FDB\u7684\u6280\u672F\u4E86\u3002</li><li>\u7ACB\u4F53\u58F0\uFF1A\u5355\u58F0\u9053\u7F3A\u4E4F\u5BF9\u58F0\u97F3\u7684\u4F4D\u7F6E\u5B9A\u4F4D\uFF0C\u800C\u7ACB\u4F53\u58F0\u6280\u672F\u5219\u5F7B\u5E95\u6539\u53D8\u4E86\u8FD9\u4E00\u72B6\u51B5\u3002\u58F0\u97F3\u5728\u5F55\u5236\u8FC7\u7A0B\u4E2D\u88AB\u5206\u914D\u5230\u4E24\u4E2A\u72EC\u7ACB\u7684\u58F0\u9053\uFF0C\u4ECE\u800C\u8FBE\u5230\u4E86\u5F88\u597D\u7684\u58F0\u97F3\u5B9A\u4F4D\u6548\u679C\u3002 \u8FD9\u79CD\u6280\u672F\u5728\u97F3\u4E50\u6B23\u8D4F\u4E2D\u663E\u5F97\u5C24\u4E3A\u6709\u7528\uFF0C\u542C\u4F17\u53EF\u4EE5\u6E05\u6670\u5730\u5206\u8FA8\u51FA\u5404\u79CD\u4E50\u5668\u6765\u81EA\u7684\u65B9\u5411\uFF0C\u4ECE\u800C\u4F7F\u97F3\u4E50\u66F4\u5BCC\u60F3\u8C61\u529B\uFF0C\u66F4\u52A0\u63A5\u8FD1\u4E8E\u4E34\u573A\u611F\u53D7\u3002\u7ACB\u4F53\u58F0\u6280\u672F\u5E7F\u6CDB\u8FD0\u7528\u4E8E\u81EA<code>Sound Blaster Pro</code>\u4EE5\u540E\u7684\u5927\u91CF\u58F0\u5361\uFF0C\u6210\u4E3A\u4E86\u5F71\u54CD\u6DF1\u8FDC\u7684\u4E00\u4E2A\u97F3\u9891\u6807\u51C6\u3002\u65F6\u81F3\u4ECA\u65E5\uFF0C\u7ACB\u4F53\u58F0\u4F9D\u7136\u662F\u8BB8\u591A\u4EA7\u54C1\u9075\u5FAA\u7684\u6280\u672F\u6807\u51C6\u3002</li><li>\u51C6\u7ACB\u4F53\u58F0\uFF1A\u51C6\u7ACB\u4F53\u58F0\u58F0\u5361\u7684\u57FA\u672C\u6982\u5FF5\u5C31\u662F\uFF1A\u5728\u5F55\u5236\u58F0\u97F3\u7684\u65F6\u5019\u91C7\u7528\u5355\u58F0\u9053\uFF0C\u800C\u653E\u97F3\u6709\u65F6\u662F\u7ACB\u4F53\u58F0\uFF0C\u6709\u65F6\u662F\u5355\u58F0\u9053\u3002\u91C7\u7528\u8FD9\u79CD\u6280\u672F\u7684\u58F0\u5361\u4E5F\u66FE\u5728\u5E02\u9762\u4E0A\u6D41\u884C\u8FC7\u4E00\u6BB5\u65F6\u95F4\uFF0C\u4F46\u73B0\u5728\u5DF2\u7ECF\u9500\u58F0\u533F\u8FF9\u4E86\u3002</li><li>\u56DB\u58F0\u9053\u73AF\u7ED5\uFF1A\u4EBA\u4EEC\u7684\u6B32\u671B\u662F\u65E0\u6B62\u5883\u7684\uFF0C\u7ACB\u4F53\u58F0\u867D\u7136\u6EE1\u8DB3\u4E86\u4EBA\u4EEC\u5BF9\u5DE6\u53F3\u58F0\u9053\u4F4D\u7F6E\u611F\u4F53\u9A8C\u7684\u8981\u6C42\uFF0C\u4F46\u662F\u968F\u7740\u6280\u672F\u7684\u8FDB\u4E00\u6B65\u53D1\u5C55\uFF0C\u5927\u5BB6\u9010\u6E10\u53D1\u73B0\u53CC\u58F0\u9053\u5DF2\u7ECF\u8D8A\u6765\u8D8A\u4E0D\u80FD\u6EE1\u8DB3\u6211\u4EEC\u7684\u9700\u6C42\u3002 \u7531\u4E8E<code>PCI</code>\u58F0\u5361\u7684\u51FA\u73B0\u5E26\u6765\u4E86\u8BB8\u591A\u65B0\u7684\u6280\u672F\uFF0C\u5176\u4E2D\u53D1\u5C55\u6700\u4E3A\u795E\u901F\u7684\u5F53\u6570\u4E09\u7EF4\u97F3\u6548\u3002\u4E09\u7EF4\u97F3\u6548\u7684\u4E3B\u65E8\u662F\u4E3A\u4EBA\u4EEC\u5E26\u6765\u4E00\u4E2A\u865A\u62DF\u7684\u58F0\u97F3\u73AF\u5883\uFF0C\u901A\u8FC7\u7279\u6B8A\u7684HRTF\u6280\u672F\u8425\u9020\u4E00\u4E2A\u8D8B\u4E8E\u771F\u5B9E\u7684\u58F0\u573A\uFF0C \u4ECE\u800C\u83B7\u5F97\u66F4\u597D\u7684\u6E38\u620F\u542C\u89C9\u6548\u679C\u548C\u58F0\u573A\u5B9A\u4F4D\u3002\u800C\u8981\u8FBE\u5230\u597D\u7684\u6548\u679C\uFF0C\u4EC5\u4EC5\u4F9D\u9760\u4E24\u4E2A\u97F3\u7BB1\u662F\u8FDC\u8FDC\u4E0D\u591F\u7684\uFF0C\u6240\u4EE5\u7ACB\u4F53\u58F0\u6280\u672F\u5728\u4E09\u7EF4\u97F3\u6548\u9762\u524D\u5C31\u663E\u5F97\u6349\u895F\u89C1\u8098\u4E86\uFF0C\u4F46\u56DB\u58F0\u9053\u73AF\u7ED5\u97F3\u9891\u6280\u672F\u5219\u5F88\u597D\u7684\u89E3\u51B3\u4E86\u8FD9\u4E00\u95EE\u9898\u3002 \u56DB\u58F0\u9053\u73AF\u7ED5\u89C4\u5B9A\u4E864\u4E2A\u53D1\u97F3\u70B9\uFF1A\u524D\u5DE6\u3001\u524D\u53F3\uFF0C\u540E\u5DE6\u3001\u540E\u53F3\uFF0C\u542C\u4F17\u5219\u88AB\u5305\u56F4\u5728\u8FD9\u4E2D\u95F4\u3002\u540C\u65F6\u8FD8\u5EFA\u8BAE\u589E\u52A0\u4E00\u4E2A\u4F4E\u97F3\u97F3\u7BB1\uFF0C\u4EE5\u52A0\u5F3A\u5BF9\u4F4E\u9891\u4FE1\u53F7\u7684\u56DE\u653E\u5904\u7406(\u8FD9\u4E5F\u5C31\u662F\u5982\u4ECA4.1\u58F0\u9053\u97F3\u7BB1\u7CFB\u7EDF\u5E7F\u6CDB\u6D41\u884C\u7684\u539F\u56E0)\u3002 \u5C31\u6574\u4F53\u6548\u679C\u800C\u8A00\uFF0C\u56DB\u58F0\u9053\u7CFB\u7EDF\u53EF\u4EE5\u4E3A\u542C\u4F17\u5E26\u6765\u6765\u81EA\u591A\u4E2A\u4E0D\u540C\u65B9\u5411\u7684\u58F0\u97F3\u73AF\u7ED5\uFF0C\u53EF\u4EE5\u83B7\u5F97\u8EAB\u4E34\u5404\u79CD\u4E0D\u540C\u73AF\u5883\u7684\u542C\u89C9\u611F\u53D7\uFF0C\u7ED9\u7528\u6237\u4EE5\u5168\u65B0\u7684\u4F53\u9A8C\u3002\u5982\u4ECA\u56DB\u58F0\u9053\u6280\u672F\u5DF2\u7ECF\u5E7F\u6CDB\u878D\u5165\u4E8E\u5404\u7C7B\u4E2D\u9AD8\u6863\u58F0\u5361\u7684\u8BBE\u8BA1\u4E2D\uFF0C \u6210\u4E3A\u672A\u6765\u53D1\u5C55\u7684\u4E3B\u6D41\u8D8B\u52BF\u3002</li><li>5.1\u58F0\u9053:5.1\u58F0\u9053\u5DF2\u5E7F\u6CDB\u8FD0\u7528\u4E8E\u5404\u7C7B\u4F20\u7EDF\u5F71\u9662\u548C\u5BB6\u5EAD\u5F71\u9662\u4E2D\uFF0C\u4E00\u4E9B\u6BD4\u8F83\u77E5\u540D\u7684\u58F0\u97F3\u5F55\u5236\u538B\u7F29\u683C\u5F0F\uFF0C\u8B6C\u5982\u675C\u6BD4<code>AC-3</code>\uFF08<code>Dolby Digital</code>\uFF09\u3001<code>DTS</code>\u7B49\u90FD\u662F\u4EE55.1\u58F0\u97F3\u7CFB\u7EDF\u4E3A\u6280\u672F\u84DD\u672C\u7684\u3002\u5176\u5B9E5.1\u58F0\u97F3\u7CFB\u7EDF\u6765\u6E90\u4E8E4.1\u73AF\u7ED5\uFF0C\u4E0D\u540C\u4E4B\u5904\u5728\u4E8E\u5B83\u589E\u52A0\u4E86\u4E00\u4E2A\u4E2D\u7F6E\u5355\u5143\u3002\u8FD9\u4E2A\u4E2D\u7F6E\u5355\u5143\u8D1F\u8D23\u4F20\u9001\u4F4E\u4E8E<code>80Hz</code>\u7684\u58F0\u97F3\u4FE1\u53F7\uFF0C\u5728\u6B23\u8D4F\u5F71\u7247\u65F6\u6709\u5229\u4E8E\u52A0\u5F3A\u4EBA\u58F0\uFF0C\u628A\u5BF9\u8BDD\u96C6\u4E2D\u5728\u6574\u4E2A\u58F0\u573A\u7684\u4E2D\u90E8\uFF0C\u4EE5\u589E\u52A0\u6574\u4F53\u6548\u679C\u3002\u76F8\u4FE1\u6BCF\u4E00\u4E2A\u771F\u6B63\u4F53\u9A8C\u8FC7<code>DolbyAC-3</code>\u97F3\u6548\u7684\u670B\u53CB\u90FD\u4F1A\u4E3A5.1\u58F0\u9053\u6240\u6298\u670D\u3002\u5343\u4E07\u4E0D\u8981\u4EE5\u4E3A5.1\u5DF2\u7ECF\u662F\u73AF\u7ED5\u7ACB\u4F53\u58F0\u7684\u9876\u5CF0\u4E86\uFF0C\u66F4\u5F3A\u5927\u76847.1\u7CFB\u7EDF\u5DF2\u7ECF\u51FA\u73B0\u4E86\u3002 \u5B83\u57285.1\u7684\u57FA\u7840\u4E0A\u53C8\u589E\u52A0\u4E86\u4E2D\u5DE6\u548C\u4E2D\u53F3\u4E24\u4E2A\u53D1\u97F3\u70B9\uFF0C\u4EE5\u6C42\u8FBE\u5230\u66F4\u52A0\u5B8C\u7F8E\u7684\u5883\u754C\u3002\u7531\u4E8E\u6210\u672C\u6BD4\u8F83\u9AD8\uFF0C\u6CA1\u6709\u5E7F\u6CDB\u666E\u53CA\u3002</li></ul><h2 id="\u6D41\u5A92\u4F53\u534F\u8BAE" tabindex="-1"><a class="header-anchor" href="#\u6D41\u5A92\u4F53\u534F\u8BAE" aria-hidden="true">#</a> \u6D41\u5A92\u4F53\u534F\u8BAE</h2><ul><li><p><code>RTP</code>\u{1F626}<code>Real-time Transport Protocol</code>)\u5B9E\u65F6\u4F20\u8F93\u534F\u8BAE\uFF0C\u662F\u4E00\u79CD\u7F51\u7EDC\u4F20\u8F93\u534F\u8BAE\uFF0C\u8FD0\u884C\u5728<code>UDP</code> \u534F\u8BAE\u4E4B\u4E0A\uFF0C<code>RTP</code>\u534F\u8BAE\u8BE6\u7EC6\u8BF4\u660E\u4E86\u5728\u4E92\u8054\u7F51\u4E0A\u4F20\u9012\u97F3\u9891\u548C\u89C6\u9891\u7684\u6807\u51C6\u6570\u636E\u5305\u683C\u5F0F\u3002<code>RTP</code>\u534F\u8BAE\u5E38\u7528\u4E8E\u6D41\u5A92\u4F53\u7CFB\u7EDF\uFF08\u914D\u5408<code>RTSP</code>\u534F\u8BAE\uFF09\u3002</p></li><li><p><code>RTCP</code>:(Real-time Transport Control Protocol)\u6216<code>RTP Control Protocol</code>\u6216\u7B80\u5199<code>RTCP</code>\uFF0C\u5B9E\u65F6\u4F20\u8F93\u63A7\u5236\u534F\u8BAE\uFF0C\u662F\u5B9E\u65F6\u4F20\u8F93\u534F\u8BAE\uFF08<code>RTP</code>\uFF09\u7684\u4E00\u4E2A\u59D0\u59B9\u534F\u8BAE\u3002<code>RTCP</code>\u4E3A<code>RTP</code>\u5A92\u4F53\u6D41\u63D0\u4F9B\u4FE1\u9053\u5916(<code>out-of-band</code>)\u63A7\u5236\u3002<code>RTCP</code>\u672C\u8EAB\u5E76\u4E0D\u4F20\u8F93\u6570\u636E\uFF0C\u4F46\u548C<code>RTP</code>\u4E00\u8D77\u534F\u4F5C\u5C06\u591A\u5A92\u4F53\u6570\u636E\u6253\u5305\u548C\u53D1\u9001\u3002<code>RTCP</code> \u5B9A\u671F\u5728\u6D41\u591A\u5A92\u4F53\u4F1A\u8BDD\u53C2\u52A0\u8005\u4E4B\u95F4\u4F20\u8F93\u63A7\u5236\u6570\u636E\u3002<code>RTCP</code>\u7684\u4E3B\u8981\u529F\u80FD\u662F\u4E3A<code>RTP</code>\u6240\u63D0\u4F9B\u7684\u670D\u52A1\u8D28\u91CF\uFF08<code>Quality of Service</code>\uFF09\u63D0\u4F9B\u53CD\u9988\u3002</p></li><li><p><code>RTSP</code>\u{1F626}<code>Real Time Streaming Protocol</code>)\u5B9E\u65F6\u6D41\u4F20\u8F93\u534F\u8BAE\u5B9E\u65F6\u6D41\u4F20\u8F93\u534F\u8BAE\uFF0C\u662F\u7528\u6765\u63A7\u5236\u58F0\u97F3\u6216\u5F71\u50CF\u7684\u591A\u5A92\u4F53\u4E32\u6D41\u534F\u8BAE\uFF0C<code>RTSP</code>\u63D0\u4F9B\u4E86\u4E00\u4E2A\u53EF\u6269\u5C55\u6846\u67B6\uFF0C\u4F7F\u5B9E\u65F6\u6570\u636E\uFF0C\u5982\u97F3\u9891\u4E0E\u89C6\u9891\u7684\u53D7\u63A7\u3001\u70B9\u64AD\u6210\u4E3A\u53EF\u80FD\u3002\u8BE5\u534F\u8BAE\u5B9A\u4E49\u4E86\u4E00\u5BF9\u591A\u5E94\u7528\u7A0B\u5E8F\u5982\u4F55\u6709\u6548\u5730\u901A\u8FC7<code>IP</code>\u7F51\u7EDC\u4F20\u9001\u591A\u5A92\u4F53\u6570\u636E\u3002<code>RTSP</code> \u5728\u4F53\u7CFB\u7ED3\u6784\u4E0A\u4F4D\u4E8E<code>RTP</code>\u548C<code>RTCP</code>\u4E4B\u4E0A\uFF0C\u5B83\u4F7F\u7528<code>TCP</code>\u6216<code>UDP</code>\u5B8C\u6210\u6570\u636E\u4F20\u8F93\u3002\u4F7F\u7528<code>RTSP</code>\u65F6\uFF0C\u5BA2\u6237\u673A\u548C\u670D\u52A1\u5668\u90FD\u53EF\u4EE5\u53D1\u51FA\u8BF7\u6C42\uFF0C\u5373<code>RTSP</code>\u53EF\u4EE5\u662F\u53CC\u5411\u7684\u3002 <code>RTSP</code>\u4E0E<code>RTP</code>\u6700\u5927\u7684\u533A\u522B\u5728\u4E8E\uFF1A<code>RTSP</code>\u662F\u4E00\u79CD\u53CC\u5411\u5B9E\u65F6\u6570\u636E\u4F20\u8F93\u534F\u8BAE\uFF0C\u5B83\u5141\u8BB8\u5BA2\u6237\u7AEF\u5411\u670D\u52A1\u5668\u7AEF\u53D1\u9001\u8BF7\u6C42\uFF0C\u5982\u56DE\u653E\u3001\u5FEB\u8FDB\u3001\u5012\u9000\u7B49\u64CD\u4F5C\u3002\u5F53\u7136\uFF0C<code>RTSP</code>\u53EF\u57FA\u4E8E<code>RTP</code>\u6765\u4F20\u9001\u6570\u636E\uFF0C\u8FD8\u53EF\u4EE5\u9009\u62E9<code>TCP</code>\u3001<code>UDP</code>\u3001\u7EC4\u64AD<code>UDP</code>\u7B49\u901A\u9053\u6765\u53D1\u9001\u6570\u636E\uFF0C\u5177\u6709\u5F88\u597D\u7684\u6269\u5C55\u6027\u3002\u5B83\u65F6\u4E00\u79CD\u7C7B\u4F3C\u4E0E<code>http</code>\u534F\u8BAE\u7684\u7F51\u7EDC\u5E94\u7528\u5C42\u534F\u8BAE\u3002</p></li><li><p><code>RTMP</code>:\uFF08<code>Real Time Messaging Protocol</code>)\u5B9E\u65F6\u6D88\u606F\u4F20\u9001\u534F\u8BAE\uFF0C\u662F<code>Adobe Systems</code>\u516C\u53F8\u4E3A<code>Flash</code>\u64AD\u653E\u5668\u548C\u670D\u52A1\u5668\u4E4B\u95F4\u97F3\u9891\u3001\u89C6\u9891\u548C\u6570\u636E\u4F20\u8F93\u5F00\u53D1\u7684\u5F00\u653E\u534F\u8BAE\u3002\u534F\u8BAE\u57FA\u4E8E<code>TCP</code>\uFF0C\u662F\u4E00\u4E2A\u534F\u8BAE\u65CF\uFF0C\u5305\u62EC<code>RTMP</code>\u57FA\u672C\u534F\u8BAE\u53CA<code>RTMPT/RTMPS/RTMPE</code>\u7B49\u591A\u79CD\u53D8\u79CD\u3002<code>RTMP</code> \u662F\u4E00\u79CD\u8BBE\u8BA1\u7528\u6765\u8FDB\u884C\u5B9E\u65F6\u6570\u636E\u901A\u4FE1\u7684\u7F51\u7EDC\u534F\u8BAE\uFF0C\u4E3B\u8981\u7528\u6765\u5728<code>Flash/AIR</code>\u5E73\u53F0\u548C\u652F\u6301<code>RTMP</code>\u534F\u8BAE\u7684\u6D41\u5A92\u4F53/\u4EA4\u4E92\u670D\u52A1\u5668\u4E4B\u95F4\u8FDB\u884C\u97F3\u89C6\u9891\u548C\u6570\u636E\u901A\u4FE1\u3002</p></li><li><p><code>HLS</code>\u{1F626}<code>HTTP Live Streaming</code>)\u662F\u82F9\u679C\u516C\u53F8\u5B9E\u73B0\u7684\u57FA\u4E8E<code>HTTP</code>\u7684\u6D41\u5A92\u4F53\u4F20\u8F93\u534F\u8BAE\uFF0C\u53EF\u5B9E\u73B0\u6D41\u5A92\u4F53\u7684\u76F4\u64AD\u548C\u70B9\u64AD\uFF0C\u4E3B\u8981\u5E94\u7528\u5728<code>IOS</code>\u7CFB\u7EDF\uFF0C\u4E3A<code>IOS</code>\u8BBE\u5907\u63D0\u4F9B\u97F3\u89C6\u9891\u76F4\u64AD\u548C\u70B9\u64AD\u65B9\u6848\u3002<code>HLS</code>\u70B9\u64AD\uFF0C\u57FA\u672C\u4E0A\u5C31\u662F\u5E38\u89C1\u7684\u5206\u6BB5<code>HTTP</code>\u70B9\u64AD\uFF0C\u4E0D\u540C\u5728\u4E8E\uFF0C\u5B83\u7684\u5206\u6BB5\u975E\u5E38\u5C0F\u3002\u76F8\u5BF9\u4E8E\u5E38\u89C1\u7684\u6D41\u5A92\u4F53\u76F4\u64AD\u534F\u8BAE\uFF0C\u4F8B\u5982<code>RTMP</code>\u534F\u8BAE\u3001<code>RTSP</code>\u534F\u8BAE\u3001<code>MMS</code>\u534F\u8BAE\u7B49\uFF0C<code>HLS</code>\u76F4\u64AD\u6700\u5927\u7684\u4E0D\u540C\u5728\u4E8E\uFF0C\u76F4\u64AD\u5BA2\u6237\u7AEF\u83B7\u53D6\u5230\u7684\uFF0C\u5E76\u4E0D\u662F\u4E00\u4E2A\u5B8C\u6574\u7684\u6570\u636E\u6D41\u3002<code>HLS</code>\u534F\u8BAE\u5728\u670D\u52A1\u5668\u7AEF\u5C06 \u76F4\u64AD\u6570\u636E\u6D41\u5B58\u50A8\u4E3A\u8FDE\u7EED\u7684\u3001\u5F88\u77ED\u65F6\u957F\u7684\u5A92\u4F53\u6587\u4EF6\uFF08<code>MPEG-TS</code>\u683C\u5F0F\uFF09\uFF0C\u800C\u5BA2\u6237\u7AEF\u5219\u4E0D\u65AD\u7684\u4E0B\u8F7D\u5E76\u64AD\u653E\u8FD9\u4E9B\u5C0F\u6587\u4EF6\u3002\u56E0\u4E3A\u670D\u52A1\u5668\u7AEF\u603B\u662F\u4F1A\u5C06\u6700\u65B0\u7684\u76F4\u64AD\u6570\u636E\u751F\u6210\u65B0\u7684\u5C0F\u6587\u4EF6\uFF0C\u8FD9\u6837\u5BA2\u6237\u7AEF\u53EA\u8981\u4E0D\u505C\u7684\u6309\u987A\u5E8F\u64AD\u653E\u4ECE\u670D\u52A1\u5668\u83B7\u53D6\u5230\u7684\u6587\u4EF6\uFF0C\u5C31\u5B9E\u73B0\u4E86\u76F4\u64AD\u3002\u7531\u6B64\u53EF\u89C1\uFF0C\u57FA\u672C\u4E0A\u53EF\u4EE5\u8BA4\u4E3A\uFF0C<code>HLS</code>\u662F\u4EE5\u70B9\u64AD\u7684\u6280\u672F\u65B9\u5F0F\u6765\u5B9E\u73B0\u76F4\u64AD\u3002\u7531\u4E8E\u6570\u636E\u901A\u8FC7<code>HTTP</code>\u534F\u8BAE\u4F20\u8F93\uFF0C\u6240\u4EE5\u5B8C\u5168\u4E0D\u7528\u8003\u8651\u9632\u706B\u5899\u6216\u8005\u4EE3\u7406\u7684\u95EE\u9898\uFF0C\u800C\u4E14\u5206\u6BB5\u6587\u4EF6\u7684\u65F6\u957F\u5F88\u77ED\uFF0C\u5BA2\u6237\u7AEF\u53EF\u4EE5\u5F88\u5FEB\u7684\u9009\u62E9\u548C\u5207\u6362\u7801\u7387\uFF0C\u4EE5\u9002\u5E94\u4E0D\u540C\u5E26\u5BBD\u6761\u4EF6\u4E0B\u7684\u64AD\u653E\u3002\u4E0D\u8FC7<code>HLS</code>\u7684\u8FD9\u79CD\u6280\u672F\u7279\u70B9\uFF0C\u51B3\u5B9A\u4E86\u5B83\u7684\u5EF6\u8FDF\u4E00\u822C\u603B\u662F\u4F1A\u9AD8\u4E8E\u666E\u901A\u7684\u6D41\u5A92\u4F53\u76F4\u64AD\u534F\u8BAE\u3002</p></li><li><p><code>HTTP</code>\u{1F626}<code>HyperText Transfer Protocol</code>)\u8D85\u6587\u672C\u4F20\u8F93\u534F\u8BAE\uFF0C\u8FD0\u884C\u5728<code>TCP</code>\u4E4B\u4E0A\uFF0C\u8FD9\u4E2A\u534F\u8BAE\u662F\u5927\u5BB6\u975E\u5E38\u719F\u6089\u7684\uFF0C\u5B83\u4E5F\u53EF\u4EE5\u7528\u5230\u89C6\u9891\u4E1A\u52A1\u4E2D\u6765\u3002</p></li></ul><p><em><strong>\u89C6\u9891\u7F16\u7801\u6807\u51C6\u548C\u97F3\u9891\u7F16\u7801\u6807\u51C6\u662F<code>H.264</code>\u548C<code>AAC</code>\uFF0C\u8FD9\u4E24\u79CD\u6807\u51C6\u5206\u522B\u662F\u5F53\u4ECA\u5B9E\u9645\u5E94\u7528\u4E2D\u7F16\u7801\u6548\u7387\u6700\u9AD8\u7684\u89C6\u9891\u6807\u51C6\u548C\u97F3\u9891\u6807\u51C6\u3002</strong></em></p><p>\u76F8\u5173\u77E5\u8BC6:</p>',24),P={href:"https://en.wikipedia.org/wiki/Comparison_of_video_container_formats",target:"_blank",rel:"noopener noreferrer"},p=c("\u5C01\u88C5\u683C\u5F0F"),M={href:"https://en.wikipedia.org/wiki/Comparison_of_video_codecs",target:"_blank",rel:"noopener noreferrer"},T=c("\u89C6\u9891\u7F16\u7801\u65B9\u5F0F"),R={href:"https://en.wikipedia.org/wiki/Comparison_of_audio_coding_formats",target:"_blank",rel:"noopener noreferrer"},A=c("\u97F3\u9891\u7F16\u7801\u65B9\u5F0F"),h=e("hr",null,null,-1),m=e("ul",null,[e("li",null,"\u90AE\u7BB1 \uFF1Acharon.chui@gmail.com"),e("li",null,"Good Luck!")],-1);function u(E,V){const o=l("ExternalLinkIcon");return a(),r("div",null,[s,e("ul",null,[e("li",null,[e("a",P,[p,d(o)])]),e("li",null,[e("a",M,[T,d(o)])]),e("li",null,[e("a",R,[A,d(o)])])]),h,m])}var S=i(t,[["render",u],["__file","\u97F3\u89C6\u9891\u57FA\u7840\u77E5\u8BC6.html.vue"]]);export{S as default};
